import React from 'react'

import styled from 'styled-components';

const Container = styled.div`
  max-width: 1170px;
  margin: auto;
`;

const Row = styled.div`
  display: flex;
  flex-wrap: wrap;
`;

const Footers = styled.div`
  background-color: #24262b;
  padding: 70px 0;
`;

const FooterCol = styled.div`
  width: 25%;
  padding: 0 15px;

  @media (max-width: 767px) {
    width: 50%;
    margin-bottom: 30px;
  }

  @media (max-width: 574px) {
    width: 100%;
  }
`;

const FMenuTitle = styled.h1`
  font-size: 18px;
  color: #ffffff;
  text-transform: capitalize;
  margin-bottom: 35px;
  font-weight: 500;
  position: relative;

  ::before {
    content: '';
    position: absolute;
    left: 0;
    bottom: -10px;
    background-color: #e91e63;
    height: 2px;
    box-sizing: border-box;
    width: 50px;
  }
`;

const FList = styled.ul`
  list-style: none;
`;

const FListItem = styled.li`
  margin-bottom: 10px;

  a {
    font-size: 16px;
    text-transform: capitalize;
    color: #ffffff;
    text-decoration: none;
    font-weight: 300;
    color: #bbbbbb;
    display: block;
    transition: all 0.3s ease;

    :hover {
      color: #ffffff;
      padding-left: 8px;
    }
  }
`;

const FInput = styled.input`
  width: 100%;
  padding: 8px;
  border: none;
  border-radius: 4px;
  margin-top: 10px;
`;

const FButton = styled.button`
  background-color: #e91e63;
  color: #fff;
  border: none;
  padding: 8px 16px;
  border-radius: 4px;
  margin-top: 10px;
  cursor: pointer;
`;

const FIcon = styled.img`
  height: 40px;
  width: 40px;
  background-color: rgba(255, 255, 255, 0.2);
  margin: 0 10px 10px 0;
  text-align: center;
  line-height: 40px;
  border-radius: 50%;
  color: #ffffff;
  transition: all 0.5s ease;

  :hover {
    color: #24262b;
    background-color: #ffffff;
  }
`;

const FooterLeft = () => {
    return (
        <FooterCol>
            <div className="footerMenu">
                <FMenuTitle>About Us</FMenuTitle>
                <FList>
                    <FListItem>
                        <a href="#">Company</a>
                    </FListItem>
                    <FListItem>
                        <a href="#">Contact</a>
                    </FListItem>
                    <FListItem>
                        <a href="#">Careers</a>
                    </FListItem>
                    <FListItem>
                        <a href="#">Affiliates</a>
                    </FListItem>
                    <FListItem>
                        <a href="#">Stores</a>
                    </FListItem>
                </FList>
            </div>
            <div className="footerMenu">
                <FMenuTitle>Useful Links</FMenuTitle>
                <FList>
                    <FListItem>
                        <a href="#">FAQs</a>
                    </FListItem>
                    <FListItem>
                        <a href="#">Shipping & Returns</a>
                    </FListItem>
                    <FListItem>
                        <a href="#">Terms & Conditions</a>
                    </FListItem>
                    <FListItem>
                        <a href="#">Privacy Policy</a>
                    </FListItem>
                </FList>
            </div>
        </FooterCol>
    );
};
const FooterRight = () => {
    return (
        <FooterCol>
            <div className="footerMenu">
                <FMenuTitle>Categories</FMenuTitle>
                <FList>
                    <FListItem>
                        <a href="#">Men</a>
                    </FListItem>
                    <FListItem>
                        <a href="#">Women</a>
                    </FListItem>
                    <FListItem>
                        <a href="#">Kids</a>
                    </FListItem>
                    <FListItem>
                        <a href="#">Accessories</a>
                    </FListItem>
                    <FListItem>
                        <a href="#">Shoes</a>
                    </FListItem>
                </FList>
            </div>
            <div className="footerMenu">
                <FMenuTitle>Follow Us</FMenuTitle>
                <FList>
                    <FListItem>
                        <a href="#">
                            <FontAwesomeIcon icon={faFacebook} />
                            Facebook
                        </a>
                    </FListItem>
                    <FListItem>
                        <a href="#">
                            <FontAwesomeIcon icon={faTwitter} />
                            Twitter
                        </a>
                    </FListItem>
                    <FListItem>
                        <a href="#">
                            <FontAwesomeIcon icon={faInstagram} />
                            Instagram
                        </a>
                    </FListItem>
                </FList>
            </div>
        </FooterCol>
    );
};

const Footer = () => {
    return (
        <FooterContainer>
            <FooterLeft />
            <FooterRight />
        </FooterContainer>
    );
};

export default Footer;
