import React, { useState, useEffect } from "react";
import { ChatState } from "../Context/ChatProvider";
import Box from "@mui/material/Box";
import ArrowCircleLeftIcon from "@mui/icons-material/ArrowCircleLeft";
import UpdateGroupChatModal from "./UpdateGroupChatModal";
import "./ChattingStyling.css";
import { FormControl } from "@mui/material";
import axios from "axios";

const SingleChat = ({ fetchAgain, setFetchAgain }) => {
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [newMessage, setNewMessage] = useState();

  const { user, selectedChat, setSelectedChat } = ChatState();

  const fetchMessages = async () => {
    if (!selectedChat) return;

    try {
      const config = {
        headers: {
          Authorization: `Bearer ${user.token}`,
        },
      };
      setLoading(true);
      const { data } = await axios.get(
        `/api/message/${selectedChat._id}`,
        config
      );
      console.log(messages);
      //console.log(data);
      setMessages(data);
      setLoading(false);
    } catch (error) {
      window.alert(error.message);
    }
  };

  useEffect(() => {
    fetchMessages();
  }, [selectedChat]);

  const sendMessage = async (event) => {
    if (event.key === "Enter" && newMessage) {
      try {
        const config = {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${user.token}`,
          },
        };
        setNewMessage("");
        const { data } = await axios.post(
          "/api/message",
          {
            content: newMessage,
            chatId: selectedChat._id,
          },
          config
        );
        console.log(data);

        setMessages([...messages, data]);
      } catch (error) {
        window.alert(error.message);
      }
    }
  };

  const typingHandler = (e) => {
    setNewMessage(e.target.value);
  };

  //console.log(selectedChat.chatName);
  return (
    <div className="SingleChatMain">
      {selectedChat ? (
        <>
          <text className="SingleChat">
            <ArrowCircleLeftIcon
              className="IconButton"
              onClick={() => setSelectedChat("")}
              sx={{
                display: { xs: "block", sm: "none" },
              }}
            />
            {!selectedChat.isGroupChat ? (
              <>
                <ArrowCircleLeftIcon
                  className="IconButton"
                  onClick={() => setSelectedChat("")}
                  sx={{
                    display: { xs: "none", sm: "block" },
                  }}
                />
                <img
                  src={selectedChat.users[1].pic}
                  alt={selectedChat.users[1].name}
                  style={{
                    width: "50px",
                    height: "50px",
                    borderRadius: "50%",
                    objectFit: "cover",
                    color: "black",
                    margin: "10px",
                  }}
                />
                {selectedChat.users[1].name.toUpperCase()}
              </>
            ) : (
              <>
                <ArrowCircleLeftIcon
                  className="IconButton"
                  onClick={() => setSelectedChat("")}
                  sx={{
                    display: { xs: "none", sm: "block" },
                  }}
                />
                {selectedChat.chatName.toUpperCase()}
                <UpdateGroupChatModal
                  fetchAgain={fetchAgain}
                  setFetchAgain={setFetchAgain}
                />
              </>
            )}
          </text>
          <Box
            display="flex"
            flexDirection="column"
            justifyContent="flex-end"
            padding="3px"
            width="100%"
            height="100%"
            borderRadius="large"
            overflow="hidden"
          >
            {loading ? (
              <Box
                display="flex"
                justifyContent="center"
                alignItems="center"
                height="100%"
              >
                <img
                  style={{
                    width: "100%",
                    height: "auto",
                    maxHeight: "80vh",
                    paddingLeft: "50px",
                  }}
                  src="./Images/loading.gif"
                  alt="loading"
                />
              </Box>
            ) : (
              <div>{/* Messages */}</div>
            )}
            <FormControl
              onKeyDown={sendMessage}
              isRequired
              style={{ marginBottom: "61px" }}
              width="100%"
            >
              <input
                varient="filled"
                placeholder="Enter a message...."
                bg="#E0E0E0"
                color="primary"
                onChange={typingHandler}
                value={newMessage}
                style={{
                  width: "99%",
                  display: "flex",
                  height: "40px",
                  borderRadius: "5px",
                  marginBottom: "3px",
                  borderColor: "linear-gradient(to right, #ff0000, #00ff00);",
                }}
              />
            </FormControl>
          </Box>
        </>
      ) : (
        <Box
          display="flex"
          alignItems="center"
          justifyContent="center"
          height="100%"
        >
          <text fontSize="28px">Click on a user to start chatting</text>
        </Box>
      )}
    </div>
  );
};

export default SingleChat;
