import Box from "@mui/material/Box";
import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";

const Community = () => {
  const navigate = useNavigate();

  const logoutHandler = () => {
    localStorage.removeItem("userInfo");
    navigate("/");
  };

  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await axios.get("/api/community/allQuestions");
        setPosts(response.data);
      } catch (error) {
        console.error("Failed to fetch posts:", error);
      }
    };

    fetchPosts();
  }, []);

  return (
    <>
      {/* Your existing code for the Community page */}
      <div>
        {posts.map((post) => (
          <div key={post._id} s>
            {/* Link to the QuestionDetails page with the postId as URL parameter */}
            <Link to={`/api/community/${post._id}`}>
              <div
                style={{
                  margin: "20px",
                  paddingLeft: "10px",
                  cursor: "pointer",
                  borderRadius: "15px",
                  borderStyle: "solid",
                  borderColor: "black",
                }}
              >
                <div style={{ display: "flex" }}>
                  <img
                    src="./Images/arrow.gif"
                    alt="arrow"
                    style={{ width: "3%" }}
                  />
                  <h3
                    style={{
                      padding: "10px",
                    }}
                  >
                    {post.title}
                  </h3>
                </div>

                <p style={{ marginLeft: "3%" }}>
                  <b>Description</b>
                </p>
                <p style={{ marginLeft: "3%" }}>{post.content}</p>
              </div>
            </Link>
            <p style={{ marginLeft: "3%" }}>
              <b>Answers</b>
            </p>
            <div
              style={{
                margin: "20px",
                paddingLeft: "10px",
                cursor: "pointer",
                borderRadius: "15px",
                borderStyle: "solid",
                borderColor: "black",
              }}
            >
              {post.comments.map((comment) => (
                <div key={comment._id}>
                  <p style={{ marginLeft: "3%" }}>{comment.content}</p>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </>
  );
};

export default Community;
