import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import "./Login.css";

const RegistrationForm = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [cpassword, setCpassword] = useState("");
  const [pic, setPic] = useState("");
  const [emailError, setEmailError] = useState("");

  const postDetails = (pic) => {};

  const submitHandler = (e) => {
    e.preventDefault();
    if (!email.match(/^[a-z0-9](\.?[a-z0-9]){5,}@g(oogle)?mail\.com$/)) {
      setEmailError("Invalid email address");
      return;
    }
    setEmailError("");
    // Code for form submission goes here
  };

  return (
    <div className="login-signup-main">
      <h2>Don't have an Account</h2>
      <h2>Signup Now</h2>
      <div className="card">
        <img
          src="./Images/signup.png"
          alt="Signupimage"
          className="loginsignupimage"
        />
        <form method="POST" id="register-form">
          <label htmlFor="username">Username:</label>
          <input
            name="username"
            type="text"
            id="username"
            autoComplete="off"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />

          <br />

          <label htmlFor="email">Email:</label>
          <input
            placeholder="abc@domain.com"
            name="email"
            type="email"
            value={email}
            id="email"
            autoComplete="off"
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          {emailError && <p className="error">{emailError}</p>}
          <br />
          <label htmlFor="password">Password:</label>
          <input
            placeholder="at least 8 characters, including at least 1 letter and 1 number"
            name="password"
            type="password"
            value={password}
            id="password"
            autoComplete="off"
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <br />
          <label htmlFor="cpassword">Confirm Password:</label>
          <input
            name="cpassword"
            type="password"
            value={cpassword}
            id="cpassword"
            autoComplete="off"
            onChange={(e) => setCpassword(e.target.value)}
            required
          />
          <br />
          <label htmlFor="pic">Upload Your Profile Picture:</label>
          <input
            name="profilePicture"
            type="file"
            id="file"
            value={pic}
            accept="image/*"
            onChange={(e) => postDetails(e.target.files[0])}
            required
          />
          <br />

          <button
            type="submit"
            className="secandorybutton"
            onClick={submitHandler}
          >
            Sign Up
          </button>
          <p className="paragraph">
            Already have an Account?{" "}
            <NavLink className="login-signup-toggle" to="/login">
              Login Now
            </NavLink>
          </p>
        </form>
      </div>
    </div>
  );
};

export default RegistrationForm;
