import React, { useState } from "react";
import Button from "@material-ui/core/Button";
import axios from "axios";
import "./Courses.css";

const extractUrl = (newVideo) => {
  console.log("extractUrl", newVideo?.videoUrl);
  const tempUrl = newVideo.videoUrl;
  return tempUrl;
};

const UploadCourses = () => {
  const [video, setVideo] = useState({}); // State variable to hold the uploaded video
  const [title, setTitle] = useState("");
  const [videoUrl, setVideoUrl] = useState("");
  const [thumbnailUrl, setThumbnailUrl] = useState("");
  const [description, setDescription] = useState("");
  const [loading, setLoading] = useState(false);

  // Function to handle image uploads
  const postImageDetails = (image) => {
    setLoading(true);
    setThumbnailUrl(image);
    if (image === undefined) {
      console.log("error");
      setLoading(false);
      return;
    }
    if (
      image.type === "image/jpeg" ||
      image.type === "image/png" ||
      image.type === "image/jpg"
    ) {
      const data = new FormData();
      data.append("file", image);
      data.append("upload_preset", "Code School");
      data.append("cloud_name", "talha1244");
      fetch("https://api.cloudinary.com/v1_1/talha1244/image/upload", {
        method: "POST",
        body: data,
      })
        .then((res) => res.json())
        .then((data) => {
          setThumbnailUrl(data.url);
          setLoading(false);
        })
        .catch((err) => {
          console.log(err);
          setLoading(false);
        });
    }
  };

  // Function to handle adding a new video
  const addVideo = (video) => {
    setLoading(true);
    if (!video) {
      console.log("No video uploaded");
      return;
    }

    const data = new FormData();
    data.append("file", video);
    data.append("upload_preset", "Code School");
    data.append("cloud_name", "talha1244");

    fetch("https://api.cloudinary.com/v1_1/talha1244/video/upload", {
      method: "POST",
      body: data,
    })
      .then((res) => res.json())
      .then((data) => {
        if (!data.url) {
          console.log("Error uploading video");
          setLoading(false);
          return;
        }
        // Set the video URL state with the correct URL value
        setVideoUrl(data.url);
        console.log(data.url);
        const newVideo = {
          title: title,
          description: description,
          videoUrl: data.url,
        };

        // Set the video state with the new video object
        setVideo(newVideo);
        console.log("video:", newVideo);
        setLoading(false);
      })
      .catch((err) => {
        console.log(err);
        setLoading(false);
      });
  };

  // Function to handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);

    const videoData = {
      title: title,
      description: description,
      videoUrl: video.videoUrl, // include the video URL in the videoData object
    };

    const data = {
      title,
      description,
      thumbnailUrl,
      video: videoData,
    };

    console.log(data);
    // Get the authentication token from wherever it's stored in your app
    const storedData = localStorage.getItem("userInfo"); // assuming it's stored in localStorage

    // Parse the string back to an object
    const parsedData = JSON.parse(storedData);

    // Access the token and userId values from the parsed object
    const token = parsedData.token;

    // Make the API call to post the course data
    axios
      .post(
        "/api/courses",
        {
          title,
          description,
          thumbnailUrl,
          videos: videoData,
        },

        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      )
      .then((res) => {
        console.log(videos);
        console.log(res);
        setLoading(false);
      })
      .catch((err) => {
        console.log(err);
        setLoading(false);
      });
  };

  // Function to handle adding a new video input field
  const handleAddVideo = () => {
    setVideos([...videos, { title: "", description: "", url: "" }]);
  };

  // Function to handle removing a video input field
  const handleRemoveVideo = (index) => {
    const newVideos = [...videos];
    newVideos.splice(index, 1);
    setVideos(newVideos);
  };

  // Function to handle changing the title and description of a video
  const handleVideoChange = (index, event) => {
    const { name, value } = event.target;
    const newVideos = [...videos];
    newVideos[index][name] = value;
    setVideos(newVideos);
  };

  return (
    <div className="upload-container">
      <div className="ChildUploadCourseBlock">
        <h2 style={{ color: "#da4ea2", textAlign: "center", fontSize: "28px" }}>
          Upload a Course
        </h2>
        <form onSubmit={handleSubmit}>
          <label htmlFor="title">Title</label>
          <input
            type="text"
            id="title"
            name="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
          <label htmlFor="description">Description</label>
          <textarea
            id="description"
            name="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          ></textarea>

          <label htmlFor="thumbnail">Thumbnail</label>
          <input
            type="file"
            id="thumbnail"
            name="thumbnail"
            accept="image/png, image/jpeg, image/jpg"
            onChange={(e) => postImageDetails(e.target.files[0])}
            required
          />

          {/* Only allow one video upload */}
          <label htmlFor="video">Video</label>
          <input
            type="file"
            accept="video/mp4,video/x-m4v,video/*"
            onChange={(e) => addVideo(e.target.files[0])}
          />

          {/* Only allow one video upload */}
          <div className="videos-container">
            <label>Course Videos</label>
            {videos.map((video, index) => (
              <div key={index} className="video-input-container">
                <input
                  type="text"
                  placeholder="Video Title"
                  name="title"
                  value={video.title}
                  onChange={(e) => handleVideoChange(index, e)}
                />
                <input
                  type="text"
                  placeholder="Video Description"
                  name="description"
                  value={video.description}
                  onChange={(e) => handleVideoChange(index, e)}
                />
                <input
                  type="file"
                  accept="video/mp4,video/x-m4v,video/*"
                  onChange={(e) => handleVideoFileChange(index, e)}
                />
                {videos.length > 1 && (
                  <button
                    type="button"
                    onClick={() => handleRemoveVideo(index)}
                  >
                    Remove
                  </button>
                )}
              </div>
            ))}
            <button type="button" onClick={handleAddVideo}>
              Add Video
            </button>
          </div>
          <Button variant="contained" color="primary" type="submit">
            Submit
          </Button>
        </form>
      </div>
    </div>
  );
};

export default UploadCourses;
