import React, { useState } from "react";
import "./Login.css";

const RegisterForm = ({ onRegister }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  const handleUsernameChange = (e) => {
    setUsername(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleConfirmPasswordChange = (e) => {
    setConfirmPassword(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Check if password matches confirmPassword
    if (password !== confirmPassword) {
      alert("Passwords do not match!");
      return;
    }
    // Call onRegister function with username and password
    onRegister(username, password);
  };

  return (
    <form className="form" onSubmit={handleSubmit}>
      <h2>Register</h2>
      <div className="form-control">
        <label htmlFor="username">Username:</label>
        <input
          type="text"
          id="username"
          value={username}
          onChange={handleUsernameChange}
          required
        />
      </div>
      <div className="form-control">
        <label htmlFor="password">Password:</label>
        <input
          type="password"
          id="password"
          value={password}
          onChange={handlePasswordChange}
          minLength="6"
          required
        />
      </div>
      <div className="form-control">
        <label htmlFor="confirm-password">Confirm Password:</label>
        <input
          type="password"
          id="confirm-password"
          value={confirmPassword}
          onChange={handleConfirmPasswordChange}
          minLength="6"
          required
        />
      </div>
      <button type="submit">Register</button>
    </form>
  );
};

const LoginForm = ({ onLogin }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleUsernameChange = (e) => {
    setUsername(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Call onLogin function with username and password
    onLogin(username, password);
  };

  return (
    <form className="form" onSubmit={handleSubmit}>
      <h2>Login</h2>
      <div className="form-control">
        <label htmlFor="username">Username:</label>
        <input
          type="text"
          id="username"
          value={username}
          onChange={handleUsernameChange}
          required
        />
      </div>
      <div className="form-control">
        <label htmlFor="password">Password:</label>
        <input
          type="password"
          id="password"
          value={password}
          onChange={handlePasswordChange}
          minLength="6"
          required
        />
      </div>
      <button type="submit">Login</button>
    </form>
  );
};

const RegisterationForm = () => {
  const [isRegistering, setIsRegistering] = useState(true);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState("");

  const handleRegister = (username, password) => {
    // Send registration request to server (mocked with a console.log here)
    console.log(`Registered user ${username} with password ${password}`);
    // Set isLoggedIn to true and setUsername to the registered username
    setIsLoggedIn(true);
    setUsername(username);
  };

  const handleLogin = (username, password) => {
    // Send login request to server (mocked with a console.log here)
    console.log(`Logged in user ${username} with password ${password}`);
    // Set isLoggedIn to true and setUsername to the logged in username
    setIsLoggedIn(true);
    setUsername(username);
  };

  const handleLogout = () => {
    // Log out the user by resetting isLoggedIn and setUsername states
    setIsLoggedIn(false);
    setUsername("");
  };

  return (
    <div className="app">
      <h1>Welcome to my app</h1>
      {isLoggedIn ? (
        <div>
          <p>You are logged in as {username}</p>
          <button onClick={handleLogout}>Logout</button>
        </div>
      ) : (
        <>
          {isRegistering ? (
            <RegisterForm onRegister={handleRegister} />
          ) : (
            <LoginForm onLogin={handleLogin} />
          )}
          <p>
            {isRegistering
              ? "Already have an account?"
              : "Don't have an account?"}{" "}
            <button onClick={() => setIsRegistering(!isRegistering)}>
              {isRegistering ? "Login" : "Register"}
            </button>
          </p>
        </>
      )}
    </div>
  );
};

export default RegisterationForm;
