import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const QuestionDetails = () => {
  const [question, setQuestion] = useState(null);
  const [commentInput, setCommentInput] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const { postId } = useParams();

  const handleCommentInputChange = (event) => {
    setCommentInput(event.target.value);
  };

  const handleSubmitComment = async () => {
    try {
      setIsLoading(true);
      const storedData = localStorage.getItem("userInfo");

      const parsedData = JSON.parse(storedData);
      const token = parsedData.token;
      const pic = parsedData.pic;
      console.log(token);
      console.log(pic);

      const newComment = { content: commentInput, pic: pic };
      const updatedQuestion = { ...question };
      updatedQuestion.comments.push(newComment);
      setQuestion(updatedQuestion);

      setCommentInput("");
      localStorage.setItem(
        `question_${postId}_comments`,
        JSON.stringify(updatedQuestion.comments)
      );

      const response = await axios.post(
        `/api/community/${postId}/comments`,
        newComment,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        }
      );

      fetchQuestion();

      setIsLoading(false);
    } catch (error) {
      setIsLoading(false);
      console.error("Failed to submit comment:", error);
    }
  };

  const fetchQuestion = async () => {
    try {
      const response = await axios.get(`/api/community/${postId}`);
      setQuestion(response.data);
    } catch (error) {
      console.error("Failed to fetch question:", error);
    }
  };

  useEffect(() => {
    fetchQuestion();
  }, [postId]);

  if (!question) {
    return <div>Loading...</div>;
  }

  return (
    <div
      style={{
        backgroundColor: "white",
        borderRadius: "15px",
        padding: "15px",
      }}
    >
      <h2 style={{ color: "black", display: "flex", alignItems: "center" }}>
        <img
          src="/Images/arrow.gif"
          alt="arrow"
          style={{
            marginRight: "10px",
            width: "40px",
            height: "40px",
            borderRadius: "50%",
          }}
        />
        {question.title}
      </h2>
      <div
        style={{
          border: "1px solid black",
          padding: "15px",
          borderRadius: "5px",
        }}
      >
        <b>Description:</b>
        <div dangerouslySetInnerHTML={{ __html: question.content }}></div>
      </div>
      <p>
        <b>Answers:</b>
      </p>

      {question.comments.map((comment) => (
        <div
          key={comment._id} // Assign a unique key prop to each rendered comment
          style={{
            padding: "15px",
            borderStyle: "solid",
            borderColor: "black",
            margin: "5px",
            borderWidth: "1px",
            borderRadius: "15px",
          }}
        >
          <div
            style={{
              display: "flex",
              alignItems: "center",
              marginBottom: "10px",
            }}
          >
            <img
              src={comment.pic}
              alt="profile"
              style={{
                width: "80px",
                height: "40px",
                borderRadius: "50%",
                marginRight: "10px",
              }}
            />
            <div dangerouslySetInnerHTML={{ __html: comment.content }}></div>
          </div>
        </div>
      ))}
      <div
        style={{
          marginTop: "20px",
          display: "flex",
          alignItems: "center",
        }}
      >
        <img
          src={localStorage.getItem("userInfo").pic} // Update the user's profile picture using the stored data from localStorage
          alt="profile"
          style={{
            width: "40px",
            height: "40px",
            borderRadius: "50%",
            marginRight: "10px",
          }}
        />
        <input
          type="text"
          placeholder="Add a comment..."
          value={commentInput}
          onChange={handleCommentInputChange}
          style={{
            flex: "1",
            padding: "10px",
            borderRadius: "5px",
            marginRight: "10px",
          }}
        />
        <button
          onClick={handleSubmitComment}
          disabled={isLoading || commentInput.trim() === ""}
          style={{
            backgroundColor: "#007BFF",
            color: "white",
            border: "none",
            padding: "10px",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          {isLoading ? "Submitting..." : "Submit"}
        </button>
      </div>
    </div>
  );
};

export default QuestionDetails;
