import React, { useState, useEffect } from "react";
import * as FaIcons from "react-icons/fa";
import * as AiIcons from "react-icons/ai";
import { Link } from "react-router-dom";
import { SidebarData } from "./SidebarData";
import "./Dashboard.css";
import { IconContext } from "react-icons";
import { ChatState } from "../Chatting/Context/ChatProvider";
import axios from "axios";
const Dashboard = () => {
  const { user, selectedChat, setSelectedChat, chats, setChats } = ChatState();
  const [loggedUser, setLoggedUser] = useState();
  const [sidebar, setSidebar] = useState(true);
  const showSidebar = () => setSidebar(!sidebar);

  const fetchChats = async () => {
    try {
      const config = {
        headers: {
          Authorization: `Bearer ${user.token}`,
        },
      };

      const { data } = await axios.get("/api/chat", config);
      setChats(data);
      //chats.map((chat) => console.log(chat.users[1].email));
      //console.log(user.email);

      // data.map((user) => console.log(user.users[1].pic));
    } catch (error) {
      window.alert(error.message);
    }
  };

  useEffect(() => {
    setLoggedUser(JSON.parse(localStorage.getItem("userInfo")));
    setTimeout(() => {
      fetchChats();
      //fetchChats().then((result) => console.log(result));
    }, 1000);
    //eslint-disable-next-line
  });

  return (
    <div>
      {/* First Column */}

      <div>
        <IconContext.Provider value={{ color: "#da4ea2" }}>
          <div className="navbar">
            <Link to="#" className="menu-bars">
              <FaIcons.FaBars onClick={showSidebar} />
            </Link>
          </div>
          <nav className={sidebar ? "nav-menu active" : "nav-menu"}>
            <ul className="nav-menu-items" onClick={showSidebar}>
              <li className="navbar-toggle">
                <Link to="#" className="menu-bars">
                  <AiIcons.AiOutlineClose />
                </Link>
              </li>
              {SidebarData.map((item, index) => {
                return (
                  <li key={index} className={item.cName}>
                    <Link to={item.path}>
                      {item.icon}
                      <span>{item.title}</span>
                    </Link>
                  </li>
                );
              })}
            </ul>
          </nav>
        </IconContext.Provider>
      </div>

      {/* 2nd Column */}
      <div className="dashboard">
        <div>cards</div>

        {/* 3rd Column */}

        <div>profile</div>
      </div>
    </div>
  );
};

export default Dashboard;
