import React, { useState, useEffect } from "react";
const MyCourses = () => {
  const [courses, setCourses] = useState([]);
  const storedData = localStorage.getItem("userInfo");
  const parsedData = JSON.parse(storedData);
  const id = parsedData._id;
  useEffect(() => {
    fetch("/api/courses")
      .then((res) => res.json())
      .then((data) => setCourses(data));
  }, []);

  console.log(courses);
  console.log(id);
  return (
    <div style={{ minHeight: "100vh", backgroundColor: "white" }}>
      <div>
        <h2 style={{ color: "#da4ea2", textAlign: "center", padding: "10px" }}>
          My Courses
        </h2>

        {courses.map((course) => {
          if (course.teacher === id) {
            return (
              <>
                <div className="dashboardProfile">
                  <div>
                    {userData ? (
                      <div>
                        <img
                          src={userData.pic}
                          alt="userPic"
                          style={{
                            width: "40%",
                            borderRadius: "50%",
                            marginLeft: "30%",
                            marginBottom: "5%",
                          }}
                        />
                        <div style={{}}>
                          <p
                            style={{
                              paddingLeft: "20px",
                              borderBottom: "1px solid white",
                              cursor: "pointer",
                            }}
                          >
                            <b
                              style={{
                                color: "#da4ea2",
                                display: "inline-flex",
                              }}
                            >
                              Name:
                            </b>
                            <p
                              style={{
                                marginLeft: "20px",
                                display: "inline-flex",
                              }}
                            >
                              {userData.name}
                            </p>
                          </p>
                          <p
                            style={{
                              paddingLeft: "20px",
                              borderBottom: "1px solid white",
                              cursor: "pointer",
                            }}
                          >
                            <b
                              style={{
                                color: "#da4ea2",
                                display: "inline-flex",
                              }}
                            >
                              Email:
                            </b>
                            <p
                              style={{
                                marginLeft: "20px",
                                display: "inline-flex",
                              }}
                            >
                              {userData.email}
                            </p>
                          </p>
                          <p
                            style={{
                              paddingLeft: "20px",
                              borderBottom: "1px solid white",
                              cursor: "pointer",
                            }}
                          >
                            <b
                              style={{
                                color: "#da4ea2",
                                display: "inline-flex",
                              }}
                            >
                              Role:
                            </b>
                            <p
                              style={{
                                marginLeft: "20px",
                                display: "inline-flex",
                              }}
                            >
                              {userData.role}
                            </p>
                          </p>
                          <p style={{ paddingLeft: "20px" }}>
                            <b
                              style={{
                                color: "#da4ea2",
                                display: "inline-flex",
                              }}
                            >
                              Id:
                            </b>
                            <p
                              style={{
                                marginLeft: "20px",
                                display: "inline-flex",
                              }}
                            >
                              {userData._id}
                            </p>
                          </p>
                        </div>
                      </div>
                    ) : (
                      <p>Loading user data...</p>
                    )}
                  </div>
                </div>

                <h3
                  key={course._id}
                  style={{
                    backgroundColor: "#da4ea2",
                    border: "1px solid",
                    borderRadius: "15px",
                    padding: "5px",
                    color: "white",
                  }}
                >
                  Title: {course.title}, Price: {course.price}
                </h3>
              </>
            );
          }
        })}
      </div>
    </div>
  );
};

export default MyCourses;
