import Box from "@mui/material/Box";
import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";

const Community = () => {
  const navigate = useNavigate();

  const logoutHandler = () => {
    localStorage.removeItem("userInfo");
    navigate("/");
  };

  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await axios.get("/api/community/allQuestions");
        setPosts(response.data);
      } catch (error) {
        console.error("Failed to fetch posts:", error);
      }
    };

    fetchPosts();
  }, []);

  // Reverse the posts array to display the latest post first
  const reversedPosts = [...posts].reverse();

  return (
    <>
      <Box
        display="flex"
        justifyContent="space-between"
        alignItems="center"
        backgroundColor="white"
        width="98.5%"
        height="auto"
        padding="5px 10px 5px 10px"
      >
        <h2 className="chattingTitle">Community</h2>
        <h2 style={{ color: "black" }}>Search</h2>
        <div style={{ display: "flex" }}>
          <button className="logoutButton" onClick={logoutHandler}>
            Logout
          </button>
        </div>
      </Box>

      <div
        style={{
          backgroundColor: "white",
          marginTop: "10px",
          borderRadius: "5px",
          paddingTop: "10px",
          paddingBottom: "10px",
        }}
      >
        {reversedPosts.map((post) => (
          <div key={post._id}>
            <Link
              to={`/api/community/${post._id}`}
              style={{ textDecoration: "none", color: "black" }}
            >
              <div
                style={{
                  margin: "20px",
                  paddingLeft: "10px",
                  cursor: "pointer",
                  borderRadius: "15px",
                  borderStyle: "solid",
                  borderColor: "black",
                }}
              >
                <div style={{ display: "flex" }}>
                  <img
                    src="./Images/arrow.gif"
                    alt="arrow"
                    style={{ width: "3%", height: "auto" }}
                  />
                  <h3
                    style={{
                      padding: "10px",
                    }}
                  >
                    {post.title}
                  </h3>
                </div>

                <p style={{ marginLeft: "3%" }}>
                  <b>Description</b>
                </p>
                <p style={{ marginLeft: "3%" }}>{post.content}</p>
              </div>
            </Link>
          </div>
        ))}
      </div>
    </>
  );
};

export default Community;
