import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

import { Typography, Grid, Paper } from "@material-ui/core";

import "./Courses.css";

const CourseDetails = () => {
  const [course, setCourse] = useState("");
  const [isEnrolled, setIsEnrolled] = useState(false);
  const [review, setReview] = useState("");
  const [comments, setComments] = useState([]);
  const [editCommentId, setEditCommentId] = useState(null);
  const storedData = localStorage.getItem("userInfo");
  const parsedData = JSON.parse(storedData);
  const id = parsedData._id;
  const token = parsedData.token;
  const pic = parsedData.pic;
  const { courseId } = useParams();

  const fetchCourse = async () => {
    try {
      const response = await axios.get(`/api/courses/${courseId}`);
      setCourse(response.data);
      setComments(response.data.comments);
    } catch (error) {
      console.error("Failed to fetch question:", error);
    }
  };

  useEffect(() => {
    fetchCourse();
  }, [courseId]);

  useEffect(() => {
    if (course) {
      checkEnrollment();
    }
  }, [course]);

  const enrollUser = async () => {
    try {
      await axios.post(
        `/api/courses/${courseId}/enroll`,
        { id },
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        }
      );
      window.alert(
        "Congratulations you have successfully enroll in the course"
      );
      setIsEnrolled(true);
    } catch (error) {
      console.error("Failed to enroll student:", error);
    }
  };

  const checkEnrollment = () => {
    const userId = id;

    if (
      course.enrollments.some((enrollment) => enrollment.student === userId)
    ) {
      setIsEnrolled(true);
    }
  };

  const submitReview = async (e) => {
    e.preventDefault();
    try {
      await axios.post(
        `/api/courses/${courseId}/comments`,
        { content: review },
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        }
      );
      window.alert("Successfully uploaded a review!");
      fetchCourse();
    } catch (error) {
      console.log(error);
      window.alert("Invalid Credentials: ");
    }
  };

  const editComment = async (commentId, newContent) => {
    try {
      await axios.put(
        `/api/courses/${courseId}/comments/${commentId}`,
        { content: newContent },
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        }
      );
      window.alert("Comment updated successfully!");
      fetchCourse();
    } catch (error) {
      console.error("Failed to edit comment:", error);
    }
  };

  const deleteComment = async (commentId) => {
    try {
      await axios.delete(`/api/courses/${courseId}/comments/${commentId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
      });
      window.alert("Comment deleted successfully!");
      fetchCourse();
    } catch (error) {
      console.error("Failed to delete comment:", error);
    }
  };

  const handleEditComment = (commentId) => {
    setEditCommentId(commentId);
  };

  const handleCancelEdit = () => {
    setEditCommentId(null);
  };

  const handleReviewChange = (e) => {
    setReview(e.target.value);
  };

  const handleCommentChange = (commentId, content) => {
    setEditCommentId(commentId);
    setReview(content);
  };

  const handleCommentCancel = () => {
    setEditCommentId(null);
    setReview("");
  };

  const handleCommentSubmit = (commentId) => {
    if (editCommentId !== null) {
      editComment(editCommentId, review);
    } else {
      submitReview();
    }
    setReview("");
    setEditCommentId(null);
  };

  return (
    <div className="course-details">
      <Grid container spacing={3}>
        <Grid item xs={12} sm={12} md={8} lg={8}>
          <Paper className="course-info" elevation={3}>
            <Typography variant="h4" className="course-title">
              {course.title}
            </Typography>
            <Typography variant="subtitle1" className="course-instructor">
              Instructor: {course.instructor}
            </Typography>
            <Typography variant="subtitle1" className="course-description">
              {course.description}
            </Typography>
            <Typography variant="subtitle2" className="course-price">
              Price: ${course.price}
            </Typography>
            {!isEnrolled && (
              <button className="enroll-btn" onClick={enrollUser}>
                Enroll Now
              </button>
            )}
          </Paper>

          <Paper className="course-comments" elevation={3}>
            <Typography variant="h5">Reviews ({comments.length})</Typography>
            {comments.map((comment) => (
              <div key={comment._id} className="comment">
                <div className="comment-info">
                  <img
                    src={comment.user.pic || pic}
                    alt="user-pic"
                    className="comment-user-pic"
                  />
                  <div className="comment-user-info">
                    <Typography variant="subtitle1">
                      {comment.user.name}
                    </Typography>
                    <Typography variant="subtitle2">
                      {new Date(comment.createdAt).toDateString()}
                    </Typography>
                  </div>
                  {comment.user._id === id && (
                    <div className="comment-options">
                      <button
                        className="edit-comment-btn"
                        onClick={() =>
                          handleCommentChange(comment._id, comment.content)
                        }
                      >
                        Edit
                      </button>
                      <button
                        className="delete-comment-btn"
                        onClick={() => deleteComment(comment._id)}
                      >
                        Delete
                      </button>
                    </div>
                  )}
                </div>
                <Typography variant="body1" className="comment-content">
                  {comment.content}
                </Typography>
              </div>
            ))}

            <form onSubmit={() => handleCommentSubmit()}>
              <textarea
                placeholder="Write your review here..."
                value={review}
                onChange={handleReviewChange}
              ></textarea>
              <div className="comment-btns">
                {editCommentId !== null && (
                  <>
                    <button
                      className="comment-submit-btn"
                      onClick={() => handleCommentSubmit(editCommentId)}
                    >
                      Update
                    </button>
                    <button
                      className="comment-cancel-btn"
                      onClick={handleCancelEdit}
                    >
                      Cancel
                    </button>
                  </>
                )}
                {editCommentId === null && (
                  <button className="comment-submit-btn" type="submit">
                    Submit
                  </button>
                )}
              </div>
            </form>
          </Paper>
        </Grid>
      </Grid>
    </div>
  );
};
export default CourseDetails;
