import React, { useState } from "react";
import "./Courses.css";
const UploadCourses = () => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [pic, setPic] = useState("");
  const [loading, setLoading] = useState(false);

  const postDetails = (pics) => {
    setLoading(true);
    setPic(pics);
    if (pic === undefined) {
      console.log("Invalid Pic Format");
      return;
    }

    if (pics.type === "image/jpeg" || pics.type === "image/png") {
      const data = new FormData();
      data.append("file", pics);
      data.append("upload_preset", "Code School");
      data.append("cloud_name", "talha1244");
      fetch("https://api.cloudinary.com/v1_1/talha1244/image/upload", {
        method: "POST",
        body: data,
      })
        .then((res) => res.json())
        .then((data) => {
          setPic(data.url);

          console.log("pic:", pic);
          setLoading(false);
          // console.log("data.url:", data.url);
        })
        .catch((err) => {
          console.log(err);
          setLoading(false);
        });
    }
  };

  const submitHandler = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const config = {
        headers: {
          "Content-Type": "application/json",
        },
      };
      /*
      const { data } = await axios.post(
        "/api/user/auth",
        { name, email, password, pic, role },
        config
      );
      */
      const data = {
        title,
        description,
        pic,
      };

      console.log(data);
      localStorage.setItem("userInfo", JSON.stringify(data));

      setLoading(false);
      window.alert("Posted Successful");
    } catch (error) {
      console.log(error);
      window.alert("Invalid Registration", error);
    }
  };

  return (
    <div className="MainUploadCourseBlock">
      <div className="ChildUploadCourseBlock">
        <h2 style={{ color: "#da4ea2", textAlign: "center" }}>
          Upload Your Courses Here!
        </h2>
        <form>
          <label htmlFor="title">Title:</label>
          <input
            name="title"
            type="text"
            id="title"
            autoComplete="off"
            value={title}
            placeholder="Enter your title here..."
            onChange={(e) => setTitle(e.target.value)}
            required
          />
          <br />
          <label htmlFor="description">Description:</label>
          <textarea
            name="description"
            id="description"
            autoComplete="off"
            value={description}
            placeholder="Enter your description here..."
            onChange={(e) => setDescription(e.target.value)}
            required
            style={{
              minWidth: "47%",
              minHeight: "50%",
              fontSize: "16px",
              display: "flex",
              borderRadius: "5px",
              marginLeft: "12px",
            }}
          />
          <br />
          <label htmlFor="Thumbnail">Thumbnail:</label>
          <input
            name="Thumbnail"
            type="file"
            id="file"
            defaultValue={pic}
            accept="image/*"
            onChange={(e) => postDetails(e.target.files[0])}
            required
          />
          <button
            type="submit"
            className="secandorybutton"
            isLoading={loading}
            onClick={submitHandler}
          >
            Sign Up
          </button>
        </form>
      </div>
    </div>
  );
};

export default UploadCourses;
