      <div>
        <h3>Comments:</h3>
        <ul>
          {course.comments.map((comment) => (
            <li key={comment._id}>{comment.studentInfo.name}</li>
          ))}
          {course.comments.map((comment) => (
            <li key={comment._id}>{comment.studentInfo.pic}</li>
          ))}
        </ul>
      </div>
      {course.enrollments.map((enrollment) => (
        <li key={enrollment._id}>{enrollment.studentInfo.pic}</li>
      ))}
