import React, { useState, useEffect } from "react";

const MyCourses = () => {
  const [courses, setCourses] = useState([]);
  const storedData = localStorage.getItem("userInfo");
  const parsedData = JSON.parse(storedData);
  const id = parsedData._id;

  useEffect(() => {
    fetch("/api/courses")
      .then((res) => res.json())
      .then((data) => setCourses(data));
  }, []);

  console.log(courses);
  console.log(id);

  const course = courses.find((course) => course.teacher === id);

  return (
    <div style={{ minHeight: "100vh", backgroundColor: "white" }}>
      <div>
        <h2 style={{ color: "#da4ea2", textAlign: "center", padding: "10px" }}>
          My Courses
        </h2>

        {courses.teacherInfo && (
          <div style={{ display: "flex", justifyContent: "center" }}>
            <div style={{ width: "50%" }}>
              <div
                style={{
                  backgroundColor: "#da4ea2",
                  borderRadius: "15px",
                  padding: "20px",
                  marginBottom: "20px",
                }}
              >
                <img
                  src={teacherInfo[0].pic}
                  alt={teacherInfo[0].name}
                  style={{ width: "30%", height: "auto" }}
                />
                <h3 style={{ color: "white", margin: "10px 0" }}>
                  {teacherInfo[0].name}
                </h3>
                <p style={{ color: "white", margin: "10px 0" }}>
                  {teacherInfo[0].email}
                </p>
              </div>
            </div>
          </div>
        )}

        {courses.map((course) => {
          if (course.teacher === id) {
            return (
              <h3
                key={course._id}
                style={{
                  backgroundColor: "#da4ea2",
                  border: "1px solid",
                  borderRadius: "15px",
                  padding: "5px",
                  color: "white",
                }}
              >
                Title: {course.title}, Price: {course.price}
              </h3>
            );
          }
        })}
      </div>
    </div>
  );
};

export default MyCourses;
