import React, { useState } from "react";
import { NavLink, useNavigate } from "react-router-dom";
import axios from "axios";
import "./Login.css";

const RegistrationForm = () => {
  const navigate = useNavigate();
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [cpassword, setCpassword] = useState("");
  const [pic, setPic] = useState("");
  const [loading, setLoading] = useState(false);
  const [role, setRole] = useState("");

  //Validation the registration
  const [emailError, setEmailError] = useState("");
  const [NameError, setNameError] = useState("");
  const [PasswordError, setPasswordError] = useState("");
  const [CPasswordError, setCPasswordError] = useState("");
  const [picError, setpicError] = useState("");

  const postDetails = (pics) => {
    setLoading(true);
    setPic(pics);
    if (pic === undefined) {
      setpicError("Invalid file format");
      return;
    }
    setpicError("");

    if (pics.type === "image/jpeg" || pics.type === "image/png") {
      const data = new FormData();
      data.append("file", pics);
      data.append("upload_preset", "Code School");
      data.append("cloud_name", "talha1244");
      fetch("https://api.cloudinary.com/v1_1/talha1244/image/upload", {
        method: "POST",
        body: data,
      })
        .then((res) => res.json())
        .then((data) => {
          setPic(data.url);

          console.log("pic:", pic);
          setLoading(false);
          console.log("data.url:", data.url);
        })
        .catch((err) => {
          console.log(err);
          setLoading(false);
        });
    }
  };

  const submitHandler = async (e) => {
    e.preventDefault();
    if (!email.match(/^[a-z0-9](\.?[a-z0-9]){5,}@g(oogle)?mail.com$/)) {
      setEmailError("Invalid email address");
      return;
    }
    setEmailError("");

    if (!name.match(/^[A-Za-z0-9]{3,16}$/)) {
      setNameError(
        "Username must be between 3 and 16 characters, and contain only letters and numbers"
      );
      return;
    }
    setNameError("");

    if (
      !password.match(
        /^(?=.*[0-9])(?=.*[a-zA-Z])(?=.*[!@#$%^&*])[a-zA-Z0-9!@#$%^&*]{8,20}$/
      )
    ) {
      setPasswordError(
        `Password should be 8-20 characters and include at least 1 letter,
         1 number and 1 special character!`
      );
      return;
    }

    setPasswordError("");

    if (password !== cpassword) {
      setCPasswordError("Passwords do not match");
      return;
    }
    setCPasswordError("");

    setLoading(true);
    try {
      const config = {
        headers: {
          "Content-Type": "application/json",
        },
      };
      const { data } = await axios.post(
        "/api/user/auth",
        { name, email, password, role, pic },
        config
      );
      setLoading(false);
      navigate("/login");
    } catch (error) {
      console.log(error);
      setLoading(false);
    }
  };

  const handleRoleChange = (e) => {
    setRole(e.target.value);
  };

  return (
    <div className="login__container">
      <form onSubmit={submitHandler}>
        <div className="login__header">
          <h2>Registration</h2>
        </div>
        <div className="login__input">
          <label htmlFor="name">Name:</label>
          <input
            type="text"
            name="name"
            id="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          {NameError && <span className="login__error">{NameError}</span>}
        </div>

        <div className="login__input">
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            name="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          {emailError && <span className="login__error">{emailError}</span>}
        </div>

        <div className="login__input">
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            name="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          {PasswordError && (
            <span className="login__error">{PasswordError}</span>
          )}
        </div>

        <div className="login__input">
          <label htmlFor="cpassword">Confirm Password:</label>
          <input
            type="password"
            name="cpassword"
            id="cpassword"
            value={cpassword}
            onChange={(e) => setCpassword(e.target.value)}
          />
          {CPasswordError && (
            <span className="login__error">{CPasswordError}</span>
          )}
        </div>

        <div className="login__input">
          <label htmlFor="pic">Profile Picture:</label>
          <input
            type="file"
            name="pic"
            id="pic"
            onChange={(e) => postDetails(e.target.files[0])}
          />
          {picError && <span className="login__error">{picError}</span>}
        </div>

        <div className="login__input">
          <label htmlFor="role">Role:</label>
          <select name="role" id="role" onChange={handleRoleChange}>
            <option value="">--Select a role--</option>
            <option value="admin">Admin</option>
            <option value="user">User</option>
          </select>
        </div>

        <div className="login__btn">
          <button type="submit">{loading ? "Loading..." : "Register"}</button>
        </div>

        <div className="login__signup">
          <p>
            Already have an account? <NavLink to="/login">Login</NavLink>
          </p>
        </div>
      </form>
    </div>
  );
};

export default RegistrationForm;
