import Box from "@mui/material/Box";
import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Community = () => {
  const navigate = useNavigate();

  const logoutHandler = () => {
    localStorage.removeItem("userInfo");
    navigate("/");
  };

  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await axios.get("/api/community/allQuestions");
        setPosts(response.data);
      } catch (error) {
        console.error("Failed to fetch posts:", error);
      }
    };

    fetchPosts();
  }, []);
  return (
    <>
      <Box
        display="flex"
        justifyContent="space-between"
        alignItems="center"
        backgroundColor="white"
        width="98.5%"
        height="auto"
        padding="5px 10px 5px 10px"
      >
        <h2 className="chattingTitle">Community</h2>
        <h2 style={{ color: "black" }}>Search</h2>
        <div style={{ display: "flex" }}>
          <button className="logoutButton" onClick={logoutHandler}>
            Logout
          </button>
        </div>
      </Box>
      <div
        style={{
          backgroundColor: "white",
          marginTop: "10px",
          borderRadius: "5px",
        }}
      >
        <h2 style={{ color: "black" }}>Questions</h2>
        <div style={{ marginLeft: "10px", paddingLeft: "10px" }}>
          {posts.map((post) => (
            <div key={post._id}>
              <div style={{ display: "flex" }}>
                <img
                  src="./Images/arrow.gif"
                  alt="arrow"
                  style={{ width: "3%" }}
                />
                <h3>{post.title}</h3>
              </div>
              <p style={{ marginLeft: "3%" }}>
                <b>Description</b>
              </p>
              <p>{post.content}</p>

              {post.comments.map((comment) => (
                <div key={comment._id}>
                  <p>{comment.content}</p>
                </div>
              ))}
            </div>
          ))}
        </div>
      </div>
    </>
  );
};

export default Community;
