import React from "react";
import PropTypes from "prop-types";
import {
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  Button,
  IconButton,
  Typography,
} from "@material-ui/core";
import CloseIcon from "@material-ui/icons/Close";
import { withStyles } from "@material-ui/core/styles";

const styles = {
  modalContent: {
    height: "410px",
  },
  modalHeader: {
    fontSize: "40px",
    fontFamily: "Work sans",
    display: "flex",
    justifyContent: "center",
  },
  modalBody: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "space-between",
  },
  closeButton: {
    position: "absolute",
    top: 0,
    right: 0,
    margin: "1rem",
  },
  closeButtonIcon: {
    fontSize: "2rem",
  },
};

const ProfileModal = ({ user, children, classes }) => {
  const [isOpen, setIsOpen] = React.useState(false);

  const handleOpen = () => {
    setIsOpen(true);
  };

  const handleClose = () => {
    setIsOpen(false);
  };

  return (
    <>
      {children ? (
        <span onClick={handleOpen}>{children}</span>
      ) : (
        <IconButton className={classes.closeButton} onClick={handleOpen}>
          <VisibilityIcon className={classes.closeButtonIcon} />
        </IconButton>
      )}
      <Modal onClose={handleClose} open={isOpen} centered>
        <div className={classes.modalContent}>
          <ModalHeader className={classes.modalHeader}>
            {user.name}
            <IconButton className={classes.closeButton} onClick={handleClose}>
              <CloseIcon className={classes.closeButtonIcon} />
            </IconButton>
          </ModalHeader>
          <ModalBody className={classes.modalBody}>
            <img
              style={{ borderRadius: "50%", height: "150px", width: "150px" }}
              src={user.pic}
              alt={user.name}
            />
            <Typography
              style={{
                fontSize: "28px",
                fontFamily: "Work sans",
                margin: "1rem",
              }}
            >
              Email: {user.email}
            </Typography>
          </ModalBody>
          <ModalFooter>
            <Button onClick={handleClose}>Close</Button>
          </ModalFooter>
        </div>
      </Modal>
    </>
  );
};

ProfileModal.propTypes = {
  user: PropTypes.object.isRequired,
  children: PropTypes.node,
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(ProfileModal);
