import React, { useEffect, useState, useRef } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import Plyr from "plyr/dist/plyr";
import "plyr/dist/plyr.css";

import { Typography, Grid, Paper } from "@material-ui/core";

import "./Courses.css";
const CourseDetails = () => {
  const [course, setCourse] = useState("");
  const [isEnrolled, setIsEnrolled] = useState(false);
  const { courseId } = useParams();
  const [review, setReview] = useState("");
  const storedData = localStorage.getItem("userInfo");
  const videoRef = useRef(null);
  const playerRef = useRef(null);

  const parsedData = JSON.parse(storedData);
  const id = parsedData._id;
  const token = parsedData.token;
  const pic = parsedData.pic;
  console.log(storedData);

  const fetchCourse = async () => {
    try {
      const response = await axios.get(`/api/courses/${courseId}`);
      setCourse(response.data);
    } catch (error) {
      console.error("Failed to fetch question:", error);
    }
  };

  useEffect(() => {
    fetchCourse();
  }, [courseId]);

  useEffect(() => {
    if (course) {
      checkEnrollment();
    }

    playerRef.current = new Plyr(videoRef.current);
    return () => {
      playerRef.current.destroy();
    };
  }, [course]);

  const enrollUser = async () => {
    try {
      await axios.post(
        `/api/courses/${courseId}/enroll`,
        { id },
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        }
      );
      window.alert(
        "Congratulations you have successfully enroll in the course"
      );
      setIsEnrolled(true);
    } catch (error) {
      console.error("Failed to enroll student:", error);
    }
  };

  const checkEnrollment = () => {
    const userId = id;

    if (
      course.enrollments.some((enrollment) => enrollment.student === userId)
    ) {
      setIsEnrolled(true);
    }
  };

  if (!course) {
    return <div>Loading...</div>;
  }
  const submitReview = async (e) => {
    e.preventDefault(); // prevent default form submission behavior
    console.log(review);
    try {
      await axios.post(
        `/api/courses/${courseId}/comments`,
        { content: review },

        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        }
      );
      window.alert("Successfully uploaded a review!");
      fetchCourse();
    } catch (error) {
      console.log(error);
    }
  };

  const handleReviewChange = (e) => {
    setReview(e.target.value);
  };

  return (
    <div className="course-details-container">
      <div className="video-container">
        <video
          controls
          crossOrigin="true"
          ref={videoRef}
          poster={course.thumbnail}
        >
          <source src={course.videoUrl} type="video/mp4" />
          <source src={course.video} type="video/webm" />
        </video>
      </div>
      <div className="course-info-container">
        {!isEnrolled && (
          <button className="enroll-button" onClick={enrollUser}>
            Enroll Now
          </button>
        )}

        {isEnrolled && (
          <Typography variant="subtitle1" className="enrolled-text">
            You are enrolled in this course
          </Typography>
        )}

        <div className="review-container">
          <form onSubmit={submitReview}>
            <textarea
              className="review-textarea"
              placeholder="Write a review"
              value={review}
              onChange={handleReviewChange}
            />
            <button type="submit" className="submit-review-button">
              Submit
            </button>
          </form>
        </div>
        {course.comments.map((comment) => (
          <Paper key={comment._id} className="comment-paper">
            <Grid container wrap="nowrap" spacing={2}>
              <Grid item>
                <img src={comment.author.pic} alt="user" />
              </Grid>
              <Grid justifyContent="left" item xs zeroMinWidth>
                <Typography variant="subtitle1" className="comment-author">
                  {comment.author.name}
                </Typography>
                <Typography variant="body2" className="comment-content">
                  {comment.content}
                </Typography>
              </Grid>
            </Grid>
          </Paper>
        ))}
      </div>
    </div>
  );
};

export default CourseDetails;
