import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

import { Typography, Grid, Paper } from "@material-ui/core";

import "./Courses.css";
const CourseDetails = () => {
  const [course, setCourse] = useState("");
  const [isEnrolled, setIsEnrolled] = useState(false);
  const { courseId } = useParams();

  const storedData = localStorage.getItem("userInfo");
  const parsedData = JSON.parse(storedData);
  const id = parsedData._id;
  console.log(id);

  const fetchCourse = async () => {
    try {
      const response = await axios.get(`/api/courses/${courseId}`);
      setCourse(response.data);
    } catch (error) {
      console.error("Failed to fetch question:", error);
    }
  };

  useEffect(() => {
    fetchCourse();
  }, [courseId]);

  useEffect(() => {
    if (course) {
      checkEnrollment();
    }
  }, [course]);
  console.log(course);

  const checkEnrollment = () => {
    const userId = id;

    if (
      course.enrollments.some((enrollment) => enrollment.student === userId)
    ) {
      setIsEnrolled(true);
    }
  };

  if (!course) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <div className="CourseDeatilsMain">
        <div className="LeftCourseDetailMain">
          <div style={{ height: "300px" }}>
            <img
              src={course.thumbnailUrl}
              alt="courseThumbnail"
              style={{
                display: "block",
                margin: "auto",
                height: "100%",
                width: "80%",
                paddingTop: "30px",
                borderRadius: "15px",
              }}
            />
          </div>

          <div style={{ padding: "10%" }}>
            <h2 style={{ color: "#da4ea2", fontWeight: "bold" }}>
              {course.title}
            </h2>
            <h3 style={{ color: "white" }}>{course.description}</h3>
            <p
              style={{
                bottom: 0,
                float: "right",
                border: "1px solid #da4ea2",
                padding: "15px",
                borderRadius: "15px",
              }}
            >
              Student Enrolled: {course.enrollments.length}
            </p>
          </div>
        </div>
        <div className="RightCourseDetailMain">
          <div>
            {course.teacherInfo[0] ? (
              <div>
                <img
                  src={course.teacherInfo[0].pic}
                  alt="TeacherPic"
                  style={{
                    width: "40%",
                    borderRadius: "50%",
                    marginLeft: "30%",
                    marginBottom: "5%",
                  }}
                />
                <div style={{}}>
                  <p
                    style={{
                      paddingLeft: "20px",
                      borderBottom: "1px solid white",
                      cursor: "pointer",
                    }}
                  >
                    <b style={{ color: "#da4ea2", display: "inline-flex" }}>
                      Name:
                    </b>
                    <p style={{ marginLeft: "20px", display: "inline-flex" }}>
                      {course.teacherInfo[0].name}
                    </p>
                  </p>
                  <p
                    style={{
                      paddingLeft: "20px",
                      borderBottom: "1px solid white",
                      cursor: "pointer",
                    }}
                  >
                    <b style={{ color: "#da4ea2", display: "inline-flex" }}>
                      Email:
                    </b>
                    <p style={{ marginLeft: "20px", display: "inline-flex" }}>
                      {course.teacherInfo[0].email}
                    </p>
                  </p>

                  <p style={{ paddingLeft: "20px" }}>
                    <b style={{ color: "#da4ea2", display: "inline-flex" }}>
                      Id:
                    </b>
                    <p style={{ marginLeft: "20px", display: "inline-flex" }}>
                      {course.teacherInfo[0]._id}
                    </p>
                  </p>
                </div>
              </div>
            ) : (
              <p>Loading user data...</p>
            )}
          </div>
        </div>
      </div>
      {!isEnrolled && (
        <div className="secandoryCourseDetails">
          <>
            <div>
              <h2>Interested in this Course?</h2>
              <h2>Enrolled Now for Free</h2>
            </div>
            <button className="enrollButton">Enroll Now</button>
          </>
        </div>
      )}
      <div>
        {isEnrolled ? (
          <>
            <h2
              style={{ color: "white", textAlign: "center", fontSize: "28px" }}
            >
              Course Videos
            </h2>
            <Grid
              container
              style={{
                marginTop: "50px",
                justifyContent: "space-around",
              }}
            >
              {course.videos.map((video) => (
                <Grid item xs={10} sm={6} md={4} key={course.id}>
                  <Paper
                    elevation={3}
                    style={{ padding: "10px", position: "relative" }}
                  >
                    <video
                      src={video.videos}
                      alt={video.title}
                      controls
                      style={{ width: "100%" }}
                    />
                    <Typography variant="h5" style={{ marginTop: "10px" }}>
                      {video.title}
                    </Typography>
                    <Typography variant="body1" style={{ marginTop: "10px" }}>
                      {video.description}
                    </Typography>
                    <div
                      style={{
                        display: "flex",
                        alignItems: "center",
                        marginTop: "20px",
                        justifyContent: "flex-start",
                      }}
                    ></div>
                  </Paper>
                </Grid>
              ))}
            </Grid>
          </>
        ) : (
          <Typography
            variant="body1"
            style={{
              textAlign: "center",
              fontSize: "28px",
              border: "1px solid #da4ea2",
              padding: "5px",
              backgroundColor: "white",
            }}
          >
            Please enroll in the course to access its videos.
          </Typography>
        )}
      </div>
      <div>
        <h2
          style={{
            color: "white",
            textAlign: "center",
            padding: "12px",
            fontSize: "28px",
          }}
        >
          Check out what other thinks about this Course
        </h2>
        <div className="Course Reviews">
          {course.comments.map((comment) => (
            <div
              key={comment._id}
              style={{
                padding: "15px",
                borderStyle: "solid",
                borderColor: "white",
                margin: "5px",
                borderWidth: "1px",
                borderRadius: "15px",
                marginBottom: "20px",
                cursor: "pointer",
              }}
            >
              <div
                style={{
                  display: "flex",
                  alignItems: "center",
                }}
              >
                <img
                  src={comment.studentInfo.pic}
                  alt="profile"
                  style={{
                    width: "5%",
                    height: "auto",
                    borderRadius: "50%",
                    marginRight: "10px",
                  }}
                />
                <div style={{ color: "white" }}>{comment.content}</div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default CourseDetails;
