import React, { useState, useEffect } from "react";
import "./OnlineCompiler.css";

const OnlineCompiler = () => {
  const [inputValue, setInputValue] = useState(`print("Code School")`);
  const encodedParams = new URLSearchParams();

  const options = {
    method: "POST",
    headers: {
      "content-type": "application/x-www-form-urlencoded",
      "X-RapidAPI-Key": "66e9770a7emsh877f75c7d416558p1591b9jsnabd36b57de3e",
      "X-RapidAPI-Host": "code-compiler.p.rapidapi.com",
    },
    body: encodedParams,
  };

  const [response, setResponse] = useState();
  const [clearInput, setClearInput] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleButtonClick = () => {
    setIsLoading(true);
    encodedParams.append("LanguageChoice", "5");
    encodedParams.append("Program", inputValue);

    fetch("https://code-compiler.p.rapidapi.com/v2", options)
      .then((response) => response.json())
      .then((response) => {
        setIsLoading(false);
        setResponse(response.Result);
        setClearInput(true);
      })
      .then((response) => console.log(response))
      .catch((err) => console.error(err));
  };

  const handleClearInput = () => {
    setClearInput(false);
    setInputValue("");
  };

  useEffect(() => {
    if (clearInput) {
      handleClearInput();
    }
  }, [clearInput]);

  return (
    <div
      style={{
        backgroundImage: `url(/Images/robothand.png)`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        paddingTop: "none",
      }}
    >
      <h2>Online Compiler</h2>
      <div className="compilerMain">
        <div className="compilerContainer">
          <div className="CompilerleftSide">
            <textarea
              value={inputValue}
              onChange={handleInputChange}
              className="codeinput"
            />
            <div className="compilerButtonMain">
              <button onClick={handleButtonClick} className="compilerButton">
                Submit
              </button>
            </div>
          </div>
          <div className="CompilerRightSide">
            <h2>Result</h2>
            <div className="loadingCompiler">
              {isLoading ? (
                <div class="progress">
                  <div className="color"></div>
                </div>
              ) : (
                <div className="compilerResult">
                  {response && (
                    <div>
                      <p>{response}</p>
                    </div>
                  )}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default OnlineCompiler;
