import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom"; // import useParams hook from react-router-dom

const QuestionDetails = () => {
  const [question, setQuestion] = useState(null);
  const { postId } = useParams(); // use useParams hook to get postId from URL params

  useEffect(() => {
    const fetchQuestion = async () => {
      try {
        const response = await axios.get(`/api/community/${postId}`); // use postId from URL params
        setQuestion(response.data);
      } catch (error) {
        console.error("Failed to fetch question:", error);
      }
    };

    fetchQuestion();
  }, [postId]); // postId as dependency in useEffect

