import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import axios from "axios";
import "./Courses.css";
const useStyles = makeStyles(() => ({
  button: {
    backgroundColor: "#da4ea2",
    color: "white",
    "&:hover": {
      backgroundColor: "#c6387e",
    },
  },
}));
const UploadCourses = () => {
  const classes = useStyles();
  const [videos, setVideos] = useState([]); // State variable to hold uploaded videos
  const [title, setTitle] = useState("");
  const [pic, setPic] = useState("");
  const [description, setDescription] = useState("");
  const [loading, setLoading] = useState(false);
  // Function to handle image uploads
  const postImageDetails = (image) => {
    setLoading(true);
    setPic(image);
    if (image === undefined) {
      console.log("error");
      setLoading(false);
      return;
    }
    if (image.type === "image/jpeg" || image.type === "image/png") {
      const data = new FormData();
      data.append("file", image);
      data.append("upload_preset", "Code School");
      data.append("cloud_name", "talha1244");
      fetch("https://api.cloudinary.com/v1_1/talha1244/image/upload", {
        method: "POST",
        body: data,
      })
        .then((res) => res.json())
        .then((data) => {
          setPic(data.url);
          setLoading(false);
        })
        .catch((err) => {
          console.log(err);
          setLoading(false);
        });
    }
  };
  // Function to handle video uploads
  const postVideoDetails = (video) => {
    setLoading(true);
    setVideos([...videos, video]);
    if (video === undefined) {
      console.log("error");
      setLoading(false);
      return;
    }
    if (
      video.type === "video/mp4" ||
      video.type === "video/quicktime" ||
      video.type === "video/x-msvideo" ||
      video.type === "video/x-ms-wmv" ||
      video.type === "video/webm" ||
      video.type === "video/ogg"
    ) {
      const data = new FormData();
      data.append("file", video);
      data.append("upload_preset", "Code School");
      data.append("cloud_name", "talha1244");
      fetch("https://api.cloudinary.com/v1_1/talha1244/video/upload", {
        method: "POST",
        body: data,
      })
        .then((res) => res.json())
        .then((data) => {
          setVideos([...videos, data.url]);
          setLoading(false);
        })
        .catch((err) => {
          console.log(err);
          setLoading(false);
        });
    }
  };
  // Function to handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);
    const data = {
      title,
      pic,
      description,
      videos,
    };
    console.log(data);
    /*
    axios
      .post("/api/upload", data)
      .then((res) => {
        console.log(res);
        setLoading(false);
        // Do something after successful submission
      })
      .catch((err) => {
        console.log(err);
        setLoading(false);
      });
      */
  };
  return (
    <div className="MainUploadCourseBlock">
      <div className="ChildUploadCourseBlock">
        <h2 style={{ color: "#da4ea2", textAlign: "center" }}>
          Upload Courses
        </h2>
        <form onSubmit={handleSubmit}>
          <label htmlFor="title">Title:</label>

          <input
            name="title"
            type="text"
            id="title"
            autoComplete="off"
            value={title}
            placeholder="Enter your title here..."
            onChange={(e) => setTitle(e.target.value)}
            required
          />
          <br />
          <label htmlFor="description">Description:</label>

          <textarea
            name="description"
            id="description"
            autoComplete="off"
            value={description}
            placeholder="Enter your description here..."
            onChange={(e) => setDescription(e.target.value)}
            required
            style={{
              minWidth: "47%",
              minHeight: "50%",
              fontSize: "16px",
              display: "flex",
              borderRadius: "5px",
              marginLeft: "12px",
            }}
          />
          <br />

          <div>
            <label htmlFor="Thumbnail">Thumbnail:</label>
            <input
              type="file"
              onChange={(e) => postImageDetails(e.target.files[0])}
            />
          </div>
          <div>
            <label htmlFor="videos">videos:</label>
            <input
              type="file"
              onChange={(e) => postVideoDetails(e.target.files[0])}
              multiple
            />
          </div>
          <Button variant="contained" type="submit" disabled={loading}>
            {loading ? "Uploading..." : "Upload"}
          </Button>
        </form>
      </div>
    </div>
  );
};
export default UploadCourses;
