import React, { useState, useEffect } from "react";
import "./OnlineCompiler.css";
import Button from "@material-ui/core/Button";
import CircularProgress from "@material-ui/core/CircularProgress";
import TextField from "@material-ui/core/TextField";
import Box from "@material-ui/core/Box";
import Grow from "@material-ui/core/Grow";

const OnlineCompiler = () => {
  const [inputValue, setInputValue] = useState(`print("Code School")`);
  const [encodedParams, setEncodedParams] = useState(new URLSearchParams());
  const [response, setResponse] = useState();
  const [clearInput, setClearInput] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleButtonClick = () => {
    setIsLoading(true);
    encodedParams.append("LanguageChoice", "5");
    encodedParams.append("Program", inputValue);

    const options = {
      method: "POST",
      headers: {
        "content-type": "application/x-www-form-urlencoded",
        "X-RapidAPI-Key": "66e9770a7emsh877f75c7d416558p1591b9jsnabd36b57de3e",
        "X-RapidAPI-Host": "code-compiler.p.rapidapi.com",
      },
      body: encodedParams,
    };

    fetch("https://code-compiler.p.rapidapi.com/v2", options)
      .then((response) => response.json())
      .then((response) => {
        setIsLoading(false);
        setResponse(response.Result);
        setClearInput(true);
      })
      .then((response) => console.log(response))
      .catch((err) => console.error(err));
  };

  const handleClearInput = () => {
    setClearInput(false);
    setInputValue("");
  };

  useEffect(() => {
    if (clearInput) {
      handleClearInput();
    }
  }, [clearInput]);

  return (
    <div
      style={{
        backgroundImage: `url(/Images/robothand.png)`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        paddingTop: "none",
      }}
    >
      <h2>Online Compiler</h2>
      <div className="compilerMain">
        <div className="compilerContainer">
          <div className="CompilerleftSide">
            <Box
              display="flex"
              flexDirection="column"
              justifyContent="space-between"
              height="100%"
            >
              <TextField
                value={inputValue}
                onChange={handleInputChange}
                className="codeinput"
                variant="outlined"
                multiline
                rows={10}
                fullWidth
                InputProps={{
                  style: {
                    backgroundColor: "#f8f8f8",
                  },
                }}
              />
              <Box mt={2}>
                <Button
                  onClick={handleButtonClick}
                  className="compilerButton"
                  variant="contained"
                  color="primary"
                  disabled={isLoading}
                >
                  {isLoading ? (
                    <CircularProgress size={24} color="inherit" />
                  ) : (
                    "Submit"
                  )}
                </Button>
              </Box>
            </Box>
          </div>
          <div className="CompilerRightSide">
            <h2>Result</h2>
            <div className="loadingCompiler">
              {isLoading ? (
                <Grow in={isLoading}>
                  <CircularProgress />
                </Grow>
              ) : (
                <pre className="compilerOutput">{response}</pre>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default OnlineCompiler;
