import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams, useHistory } from "react-router-dom";
import ReactQuill from "react-quill";
import "react-quill/dist/quill.snow.css";

const QuestionDetails = () => {
  const [question, setQuestion] = useState(null);
  const [commentInput, setCommentInput] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const { postId } = useParams();
  const history = useHistory();

  const handleCommentInputChange = (event) => {
    setCommentInput(event.target.value);
  };

  const handleDeletePost = async () => {
    try {
      const storedData = localStorage.getItem("userInfo");
      const parsedData = JSON.parse(storedData);
      const token = parsedData.token;

      await axios.delete(`/api/community/${postId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
      });

      // Redirect the user to the community page after deleting the post
      history.push("/community");
    } catch (error) {
      console.error("Failed to delete post:", error);
    }
  };

  const handleSubmitComment = async () => {
    try {
      setIsLoading(true);
      const storedData = localStorage.getItem("userInfo");
      const parsedData = JSON.parse(storedData);
      const token = parsedData.token;
      const pic = parsedData.pic;

      const newComment = { content: commentInput, pic: pic };
      const updatedQuestion = { ...question };
      updatedQuestion.comments.push(newComment);
      setQuestion(updatedQuestion);
      setCommentInput("");
      localStorage.setItem(
        `question_${postId}_comments`,
        JSON.stringify(updatedQuestion.comments)
      );

      const response = await axios.post(
        `/api/community/${postId}/comments`,
        newComment,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        }
      );

      fetchQuestion();

      setIsLoading(false);
    } catch (error) {
      setIsLoading(false);
      console.error("Failed to submit comment:", error);
    }
  };

  const fetchQuestion = async () => {
    try {
      const response = await axios.get(`/api/community/${postId}`);
      setQuestion(response.data);
    } catch (error) {
      console.error("Failed to fetch question:", error);
    }
  };

  useEffect(() => {
    fetchQuestion();
  }, [postId]);

  if (!question) {
    return <div>Loading...</div>;
  }

  const storedData = localStorage.getItem("userInfo");
  const parsedData = JSON.parse(storedData);
  const userId = parsedData._id;

  return (
    <div
      style={{
        backgroundColor: "white",
        borderRadius: "15px",
        padding: "15px",
      }}
    >
      <h2 style={{ color: "#da4ea2", display: "flex", alignItems: "center" }}>
        <img
          src="/Images/arrow.gif"
          alt="arrow"
          style={{
            marginRight: "10px",
            width: "40px",
            height: "40px",
            borderRadius: "50%",
          }}
        />
        {question.title}
      </h2>
      <div
        style={{
          paddingTop: "10px",
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
        }}
      >
        <div style={{ display: "flex", alignItems: "center" }}>
          <img
            src={question.user.pic}
            alt="user"
            style={{
              width: "30px",
              height: "30px",
              borderRadius: "50%",
              marginRight: "10px",
            }}
          />
          <span>{question.user.username}</span>
        </div>
        {userId === question.user._id && (
          <button onClick={handleDeletePost} style={{ color: "#da4ea2" }}>
            Delete
          </button>
        )}
      </div>
      <hr style={{ margin: "10px 0" }} />
      <ReactQuill value={question.content} readOnly={true} />
      <hr style={{ margin: "10px 0" }} />
      <div>
        {question.comments.map((comment, index) => (
          <div key={index}>
            <div style={{ display: "flex", alignItems: "center" }}>
              <img
                src={comment.pic}
                alt="user"
                style={{
                  width: "30px",
                  height: "30px",
                  borderRadius: "50%",
                  marginRight: "10px",
                }}
              />
              <span>{comment.username}</span>
            </div>
            <ReactQuill value={comment.content} readOnly={true} />
            <hr style={{ margin: "10px 0" }} />
          </div>
        ))}
      </div>
      <div style={{ marginTop: "10px" }}>
        <textarea
          value={commentInput}
          onChange={handleCommentInputChange}
          placeholder="Add a comment..."
          style={{ width: "100%", minHeight: "100px" }}
        ></textarea>
        <button onClick={handleSubmitComment} disabled={isLoading}>
          {isLoading ? "Submitting..." : "Submit"}
        </button>
      </div>
    </div>
  );
};

export default QuestionDetails;
