import { Tooltip, Button, Menu, MenuItem, Avatar } from "@mui/material";
import PopupState, { bindTrigger, bindMenu } from "material-ui-popup-state";
import Box from "@mui/material/Box";
import React, { useState } from "react";
import "./ChattingStyling.css";
import { ChatState } from "../Context/ChatProvider";

const SideDrawer = () => {
  const [search, setSearch] = useState("");
  const [searchResult, setSearchResult] = useState([]);
  const [loading, setLoading] = useState(false);
  const [loadingChat, setLoadingChat] = useState();

  const user = ChatState();

  return (
    <>
      <Box
        display="flex"
        justifyContent="space-between"
        alignItems="center"
        backgroundColor="white"
        width="98.5%"
        height="auto"
        padding="5px 10px 5px 10px"
      >
        <Tooltip label="Search Users to Chat" hasArrow placement="bottom-end">
          <Button varient="ghost">
            <img
              src="/Images/search.png"
              alt="search"
              style={{
                width: "44px",
                height: "auto",
              }}
            />
            <p style={{ padding: "2px" }}>Search Users</p>
          </Button>
        </Tooltip>
        <h2 style={{ color: "#da4ea2" }}>Chating Feature </h2>
        <div style={{ display: "flex" }}>
          <img src={user.pic} alt="Avatar" class="avatar" />
          <button className="logoutButton">Logout</button>
        </div>
      </Box>
    </>
  );
};

export default SideDrawer;
