import React, { useState } from "react";
import axios from "axios";

const AskQuestion = () => {
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [image, setImage] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Create FormData object to send form data including the image
    const formData = new FormData();
    formData.append("title", title);
    formData.append("content", content);
    if (image) {
      formData.append("image", image);
    }

    try {
      // Get the authentication token from wherever it's stored in your app
      const token = localStorage.getItem("token"); // assuming it's stored in localStorage
      console.log("token", token);
      // Make POST request to backend API with token in headers
      const response = await axios.post(
        "/api/community/askQuestion",
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "multipart/form-data",
          },
        }
      );

      // Handle successful post creation
      console.log("Post created successfully:", response.data);

      // Clear form fields
      setTitle("");
      setContent("");
      setImage(null);
    } catch (error) {
      // Handle error
      console.error("Error creating post:", error.response.data);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      {/* Render form fields for title, content, and image */}
      <input
        type="text"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        placeholder="Title"
      />
      <textarea
        value={content}
        onChange={(e) => setContent(e.target.value)}
        placeholder="Content"
      ></textarea>
      <input type="file" onChange={(e) => setImage(e.target.files[0])} />
      <button type="submit">Ask Question</button>
    </form>
  );
};

export default AskQuestion;
