import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    height: "60vh",
    backgroundColor: "#f5f5f5",
  },
  paper: {
    padding: theme.spacing(2),
    maxWidth: "500px",
    width: "80%",
  },
  textField: {
    marginBottom: theme.spacing(2),
  },
  button: {
    backgroundColor: "#da4ea2",
    color: "white",
    "&:hover": {
      backgroundColor: "#c6387e",
    },
  },
}));

const UploadCourses = () => {
  const classes = useStyles();
  const [numVideos, setNumVideos] = useState(1); // State variable to keep track of the number of video fields

  // Function to render additional video fields when the "Add Another Video" button is clicked
  const addVideoField = () => {
    setNumVideos(numVideos + 1);
  };

  // Array to hold the video fields
  const videoFields = [];
  for (let i = 0; i < numVideos; i++) {
    videoFields.push(
      <TextField
        key={i}
        fullWidth
        variant="outlined"
        label={`Course Video URL ${i + 1}`}
        className={classes.textField}
      />
    );
  }

  return (
    <div
      style={{
        backgroundColor: "#f5f5f5",
        textAlign: "center",
        paddingTop: "40px",
      }}
    >
      <h2 style={{ color: "#da4ea2" }}>Got Some New Skills?</h2>
      <h3 style={{ color: "#da4ea2" }}>
        Help others too and share your knowledge
      </h3>
      <div className={classes.root}>
        <Paper className={classes.paper}>
          <form>
            <TextField
              fullWidth
              variant="outlined"
              label="Course Title"
              className={classes.textField}
            />
            <TextField
              fullWidth
              variant="outlined"
              label="Course Description"
              className={classes.textField}
              multiline
              rows={4}
            />
            <TextField
              fullWidth
              variant="outlined"
              label="Course Image URL"
              className={classes.textField}
            />
            {videoFields}
            <Button
              variant="contained"
              className={classes.button}
              onClick={addVideoField}
            >
              Add Another Video
            </Button>
            <Button variant="contained" className={classes.button}>
              Upload
            </Button>
          </form>
        </Paper>
      </div>
    </div>
  );
};

export default UploadCourses;
