import React, { useState, useEffect } from "react";
import Button from "@material-ui/core/Button";
import axios from "axios";
import "./Courses.css";

// Use the useEffect hook to update the new video object with the video URL
useEffect(() => {
  const index = videos.length - 1;
  const newVideos = [...videos];
  newVideos[index].url = data.url;
  setVideos(newVideos);
}, [data.url, videos]);
const UploadCourses = () => {
  const [videos, setVideos] = useState([]); // State variable to hold uploaded videos
  const [title, setTitle] = useState("");
  const [thumbnailUrl, setThumbnailUrl] = useState("");
  const [description, setDescription] = useState("");
  const [loading, setLoading] = useState(false);

  // Function to handle image uploads
  const postImageDetails = (image) => {
    setLoading(true);
    setThumbnailUrl(image);
    if (image === undefined) {
      console.log("error");
      setLoading(false);
      return;
    }
    if (
      image.type === "image/jpeg" ||
      image.type === "image/png" ||
      image.type === "image/jpg"
    ) {
      const data = new FormData();
      data.append("file", image);
      data.append("upload_preset", "Code School");
      data.append("cloud_name", "talha1244");
      fetch("https://api.cloudinary.com/v1_1/talha1244/image/upload", {
        method: "POST",
        body: data,
      })
        .then((res) => res.json())
        .then((data) => {
          setThumbnailUrl(data.url);
          setLoading(false);
        })
        .catch((err) => {
          console.log(err);
          setLoading(false);
        });
    }
  };

  // Function to handle adding a new video to the array
  const addVideo = (video) => {
    if (!video) {
      console.log("No video uploaded");
      return;
    }

    const data = new FormData();
    data.append("file", video);
    data.append("upload_preset", "Code School");
    data.append("cloud_name", "talha1244");

    fetch("https://api.cloudinary.com/v1_1/talha1244/video/upload", {
      method: "POST",
      body: data,
    })
      .then((res) => res.json())
      .then((data) => {
        if (!data.url) {
          console.log("Error uploading video");
          return;
        }

        console.log(data);

        const newVideo = {
          title: "",
          description: "",
          url: "",
        };

        // Set the videos state with the new video object
        setVideos([...videos, newVideo]);

        setLoading(false);
      })
      .catch((err) => {
        console.log(err);
        setLoading(false);
      });
  };

  // Function to handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);
    const videoData = videos.map((video) => ({
      title: video.title,
      description: video.description,
      videosUrl: video.url,
    }));

    const data = {
      title,
      description,
      thumbnailUrl,
      videos: videoData,
    };
    console.log(data);

    // Get the authentication token from wherever it's stored in your app
    const storedData = localStorage.getItem("userInfo"); // assuming it's stored in localStorage

    // Parse the string back to an object
    const parsedData = JSON.parse(storedData);

    // Access the token and userId values from the parsed object
    const token = parsedData.token;

    // Make the API call to post the course data
    axios
      .post(
        "/api/courses",
        {
          title,
          description,
          thumbnailUrl,
          videos: videoData,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      )
      .then((res) => {
        console.log(res);
        setLoading(false);
      })
      .catch((err) => {
        console.log(err);
        setLoading(false);
      });
  };

  // Function to handle adding a new video input field
  const handleAddVideo = () => {
    setVideos([...videos, { title: "", description: "", url: "" }]);
  };

  // Function to handle removing a video input field
  const handleRemoveVideo = (index) => {
    const newVideos = [...videos];
    newVideos.splice(index, 1);
    setVideos(newVideos);
  };

  // Function to handle changing the title and description of a video
  const handleVideoChange = (index, event) => {
    const { name, value } = event.target;
    const newVideos = [...videos];
    newVideos[index][name] = value;
    setVideos(newVideos);
  };

  return (
    <div className="upload-container">
      <h1>Upload a Course</h1>
      <form onSubmit={handleSubmit}>
        <label htmlFor="title">Title</label>
        <input
          type="text"
          id="title"
          name="title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <label htmlFor="description">Description</label>
        <textarea
          id="description"
          name="description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        ></textarea>

        <label htmlFor="thumbnail">Thumbnail</label>
        <input
          type="file"
          id="thumbnail"
          name="thumbnail"
          accept="image/png, image/jpeg, image/jpg"
          onChange={(e) => postImageDetails(e.target.files[0])}
          required
        />

        <label htmlFor="videos">Videos</label>
        {videos.map((video, index) => (
          <div key={index} className="video-input-container">
            <input
              type="file"
              id={`video${index}`}
              name={`video${index}`}
              accept="video/mp4"
              onChange={(e) => addVideo(e.target.files[0])}
              required
            />
            <div className="video-input-fields">
              <input
                type="text"
                name="title"
                placeholder="Title"
                value={video.title}
                onChange={(e) => handleVideoChange(index, e)}
                required
              />
              <textarea
                name="description"
                placeholder="Description"
                value={video.description}
                onChange={(e) => handleVideoChange(index, e)}
                required
              ></textarea>
            </div>
            <Button
              variant="contained"
              color="secondary"
              onClick={() => handleRemoveVideo(index)}
            >
              Remove
            </Button>
          </div>
        ))}
        <Button variant="contained" onClick={handleAddVideo}>
          Add Video
        </Button>

        <Button
          variant="contained"
          color="primary"
          type="submit"
          //disabled={loading}
        >
          {loading ? "Uploading..." : "Upload"}
        </Button>
      </form>
    </div>
  );
};
export default UploadCourses;
