import React from "react";
import { useEffect, useState } from "react";
import { ChatState } from "../Context/ChatProvider";
import axios from "axios";
import AddCircleIcon from "@mui/icons-material/AddCircle";
import { Stack, Box } from "@mui/material";
import { getSender } from "../config/ChatLogics";
import { Avatar } from "@mui/material";
import GroupChatModal from "./GroupChatModal";
import "./ChattingStyling.css";

const MyChats = ({ fetchAgain }) => {
  const { user, selectedChat, setSelectedChat, chats, setChats } = ChatState();
  const [loggedUser, setLoggedUser] = useState();

  const fetchChats = async () => {
    try {
      const config = {
        headers: {
          Authorization: `Bearer ${user.token}`,
        },
      };

      const { data } = await axios.get("/api/chat", config);
      setChats(data);
      data.map((pic) => console.log(user.user.pic));
    } catch (error) {
      window.alert(error.message);
    }
  };

  useEffect(() => {
    setLoggedUser(JSON.parse(localStorage.getItem("userInfo")));
    setTimeout(() => {
      fetchChats();
      //fetchChats().then((result) => console.log(result));
    }, 1000);
    //eslint-disable-next-line
  }, [fetchAgain]);

  return (
    <div
      style={{
        display: selectedChat ? "none" : "flex",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        paddingLeft: "20px",
        backgroundColor: "white",
        borderWidth: "10px",
        width: "100%",
        height: "100%",
        borderRadius: "2%",
      }}
    >
      <div
        style={{
          fontSize: "28px",
          color: "black",
          display: "flex",
          width: "100%",
          justifyContent: "space-between",
          alignItems: "center",
          padding: "3px",
        }}
      >
        My Chats
        <GroupChatModal>
          <button
            style={{
              display: "flex",
              padding: "3px",
              marginRight: "5px",
              marginTop: "5px",
            }}
          >
            <b style={{ marginTop: "3px", marginRight: "3px" }}>
              New Group Chat
            </b>
            <AddCircleIcon />
          </button>
        </GroupChatModal>
      </div>
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          padding: "3px",
          backgroundColor: "white",
          color: "black",
          width: "90%",
          height: "100%",
          overflow: "hidden",
          boxShadow: "rgba(149, 157, 165, 0.2) 0px 8px 24px",
          borderRadius: "10px",
          marginTop: "20px",
        }}
      >
        {chats ? (
          <Stack overflowY="scroll">
            {chats.map((chat) => (
              <Box
                onClick={() => setSelectedChat(chat)}
                cursor="pointer"
                bg={selectedChat === chat ? "#38B2AC" : "#E8E8E8"}
                color={selectedChat === chat ? "white" : "black"}
                px={3}
                py={2}
                borderRadius="lg"
                key={chat._id}
              >
                <div
                  style={{
                    display: "flex",
                    alignItems: "center",
                    width: "30px",
                    height: "30px",
                    borderRadius: "50%",
                    marginRight: "10px",
                    backgroundColor: "#eee",
                    justifyContent: "center",
                    alignItems: "center",
                    fontWeight: "bold",
                    color: "black",
                    fontSize: "16px",
                    cursor: "default",
                  }}
                >
                  {chat.pic ? (
                    <img
                      src={chats.user.pic}
                      alt={chat.username}
                      style={{
                        width: "100%",
                        height: "100%",
                        borderRadius: "50%",
                        objectFit: "cover",
                      }}
                    />
                  ) : chat.username ? (
                    chat.username.charAt(0).toUpperCase()
                  ) : (
                    ""
                  )}
                </div>
                <div
                  style={{
                    flex: 1,
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "space-between",
                    borderBottom: "1px solid #eee",
                    padding: "5px",
                  }}
                >
                  <div>
                    {!chat.isGroupChat
                      ? getSender(loggedUser, chat.users)
                      : chat.chatName}
                  </div>
                  <div style={{ fontWeight: "bold" }}>
                    {chat.lastMessage && chat.lastMessage.timeStamp
                      ? new Date(chat.lastMessage.timeStamp).toLocaleString()
                      : ""}
                  </div>
                </div>

                {/*chat.latestMessage && (
                  <text fontSize="xs">
                    <b>{chat.latestMessage.sender.name} : </b>
                    {chat.latestMessage.content.length > 50
                      ? chat.latestMessage.content.substring(0, 51) + "..."
                      : chat.latestMessage.content}
                  </text>
                    )*/}
              </Box>
            ))}
          </Stack>
        ) : (
          <p>Loading......</p>
        )}
      </div>

      {selectedChat && selectedChat.messages && (
        <div
          style={{
            backgroundColor: "white",
            height: "90%",
            width: "90%",
            overflow: "hidden",
          }}
        >
          <h2>All Chats</h2>

          <div style={{ height: "80%", overflow: "scroll" }}>
            {selectedChat.messages.map((message) => (
              <div
                key={message._id}
                style={{
                  display: "flex",
                  flexDirection: "column",
                  margin: "5px",
                }}
              >
                <div
                  style={{
                    display: "flex",
                    alignItems: "center",
                    marginBottom: "5px",
                  }}
                >
                  <Avatar src={message.sender.pic}>
                    {message.sender.username.charAt(0).toUpperCase()}
                  </Avatar>
                  <div
                    style={{
                      marginLeft: "10px",
                      fontWeight: "bold",
                    }}
                  >
                    {message.sender.username}
                  </div>
                </div>
                <div>{message.body}</div>
                <div
                  style={{
                    alignSelf: "flex-end",
                    color: "gray",
                    fontSize: "12px",
                    marginTop: "5px",
                  }}
                >
                  {/*new Date(message.timeStamp).toLocaleString()*/}
                </div>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default MyChats;
