import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import { Typography, Grid, Paper } from "@material-ui/core";

import "./Courses.css";

const CourseDetails = () => {
  const [course, setCourse] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const { courseId } = useParams();
  const [isEnrolled, setIsEnrolled] = useState(false); // new state variable

  const fetchQuestion = async () => {
    try {
      const response = await axios.get(`/api/courses/${courseId}`);
      setCourse(response.data);
    } catch (error) {
      console.error("Failed to fetch question:", error);
    }
  };

  useEffect(() => {
    fetchQuestion();
  }, [courseId]);

  useEffect(() => {
    const checkEnrollment = async () => {
      try {
        const response = await axios.get(`/api/courses/${courseId}students`);
        const enrollments = response.data;
        setIsEnrolled(enrollments.some((e) => e.courseId === courseId));
      } catch (error) {
        console.error("Failed to fetch enrollments:", error);
      }
    };

    checkEnrollment();
  }, [courseId]);

  if (!course) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <div className="CourseDeatilsMain">{/* code for course details */}</div>
      <div className="secandoryCourseDetails">
        <div>
          <div>
            <h2>Interested in this Course?</h2>
            <h2>Enroll Now for Free</h2>
          </div>
        </div>
        <div>
          {isEnrolled ? (
            <button className="enrollButton">Watch Videos</button>
          ) : (
            <button className="enrollButton">Enroll Now</button>
          )}
        </div>
      </div>
      {isEnrolled && (
        <div>
          <h2
            style={{
              color: "white",
              textAlign: "center",
              padding: "12px",
              fontSize: "28px",
            }}
          >
            Course Videos
          </h2>
          <Grid
            container
            style={{
              marginTop: "50px",
              justifyContent: "space-around",
            }}
          >
            {course.videos.map((video) => (
              <Grid item xs={10} sm={6} md={4} key={course.id}>
                {/* code for video item */}
              </Grid>
            ))}
          </Grid>
        </div>
      )}
    </div>
  );
};

export default CourseDetails;
