import React, { useState } from "react";
import axios from "axios";

const AskQuestion = () => {
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Create request body
    const requestBody = {
      title: title,
      content: content,
    };

    try {
      // Get the authentication token from wherever it's stored in your app
      const storedData = localStorage.getItem("userInfo"); // assuming it's stored in localStorage

      // Parse the string back to an object
      const parsedData = JSON.parse(storedData);

      // Access the name value
      const token = parsedData.token;

      // Make POST request to backend API with token in headers and request body
      const response = await axios.post(
        "/api/community/askQuestion",
        requestBody,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json", // Set content type to application/json
          },
        }
      );

      // Handle successful post creation
      console.log("Post created successfully:", response.data);

      // Clear form fields
      setTitle("");
      setContent("");
    } catch (error) {
      // Handle error
      console.error("Error creating post:", error.response.data);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      {/* Render form fields for title, content */}
      <input
        type="text"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        placeholder="Title"
      />
      <textarea
        value={content}
        onChange={(e) => setContent(e.target.value)}
        placeholder="Content"
      ></textarea>

      <button type="submit">Ask Question</button>
    </form>
  );
};

export default AskQuestion;
