import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import "./Courses.css";
const CourseDetails = () => {
  const [course, setCourse] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const { courseId } = useParams();

  const fetchQuestion = async () => {
    try {
      const response = await axios.get(`/api/courses/${courseId}`);
      setCourse(response.data);
    } catch (error) {
      console.error("Failed to fetch question:", error);
    }
  };

  useEffect(() => {
    fetchQuestion();
  }, [courseId]);

  if (!course) {
    return <div>Loading...</div>;
  }

  return (
    <div className="courseDetailsMain">
      <div className="CourseDetailsChild">
        <h2 style={{ color: "#da4ea2", padding: "20px 10px" }}>
          {course.title}
        </h2>
        <p style={{ color: "white", padding: "12px" }}>{course.description}</p>
      </div>
      <div className="teacher-info">
        <img
          src={course.teacherInfo[0].pic}
          alt={course.teacherInfo[0].name}
          className="teacher-pic"
        />
        <div className="teacher-name">{course.teacherInfo[0].name}</div>
        <div className="teacher-email">{course.teacherInfo[0].email}</div>
      </div>
      <img
        src={course.thumbnailUrl}
        alt={course.title}
        className="course-thumbnail"
      />
      <div className="comments">
        <h3>Comments:</h3>
        <ul className="comment-list">
          {course.comments.map((comment) => (
            <li key={comment._id}>
              <img
                src={comment.studentInfo.pic}
                alt={comment.studentInfo.name}
                className="comment-pic"
              />
              <div className="comment-name">{comment.studentInfo.name}</div>
            </li>
          ))}
        </ul>
      </div>
      <div className="enrollments">
        <h3>Enrollments:</h3>
        <ul className="enrollment-list">
          {course.enrollments.map((enrollment) => (
            <li key={enrollment._id}>
              <img
                src={enrollment.studentInfo.pic}
                alt={enrollment.studentInfo.name}
                className="enrollment-pic"
              />
              <div className="enrollment-name">
                {enrollment.studentInfo.name}
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default CourseDetails;
