import React, { useState, useEffect } from "react";
import { Container, Typography, Grid, Paper } from "@material-ui/core";

const ShowCourses = () => {
  const [courses, setCourses] = useState([]);

  useEffect(() => {
    fetch("/api/courses")
      .then((res) => res.json())
      .then((data) => setCourses(data));
  }, []);

  //console.log(courses); // Check the value of courses
  //console.log(courses.map((course) => course.title)); // Check the value of course.title for each course
  console.log(courses.map((course) => course.teacherInfo.slice(0, 20))); // Check the first 20 characters of each course title
  const teacherInfo = courses[0].teacherInfo[0];
  const name = teacherInfo.name;
  const pic = teacherInfo.pic;

  console.log(name); // Output: teacher
  console.log(pic);
  return (
    <div style={{ backgroundColor: "#f7f7f7", minHeight: "100vh" }}>
      <Container maxWidth="lg" style={{ paddingTop: "50px" }}>
        <Typography variant="h2" align="center" style={{ color: "#da4ea2" }}>
          Want to learn something new?
        </Typography>
        <Typography variant="h2" align="center" style={{ color: "#da4ea2" }}>
          Enroll Now
        </Typography>
        <img
          src="./Images/smallLine.png"
          alt="smallLine"
          style={{ width: "30%", margin: "auto", display: "block" }}
        />
        <Grid container spacing={3} style={{ marginTop: "50px" }}>
          {courses.map((course) => (
            <Grid item xs={12} sm={6} md={4} key={course.id}>
              <Paper
                elevation={3}
                style={{ padding: "20px", position: "relative" }}
              >
                <img
                  src={course.thumbnailUrl}
                  alt={course.title}
                  style={{ width: "100%" }}
                />
                <Typography variant="h5" style={{ marginTop: "10px" }}>
                  {course.title}
                </Typography>
                <Typography variant="body1" style={{ marginTop: "10px" }}>
                  {course.description}
                </Typography>
                <div
                  style={{
                    position: "absolute",
                    bottom: 0,
                    left: 0,
                    padding: "10px",
                  }}
                >
                  <img
                    src={course.teacherInfo.pic}
                    alt={course.teacherInfo.name}
                    style={{
                      width: "50px",
                      borderRadius: "50%",
                      marginRight: "10px",
                    }}
                  />
                  <Typography
                    variant="subtitle1"
                    style={{ display: "inline-block" }}
                  >
                    Uploaded by {course.teacherInfo.name}
                  </Typography>
                </div>
              </Paper>
            </Grid>
          ))}
        </Grid>
      </Container>
    </div>
  );
};

export default ShowCourses;
