import React, { useState } from "react";
import { ChatState } from "../Context/ChatProvider";
import Box from "@mui/material/Box";
import ArrowCircleLeftIcon from "@mui/icons-material/ArrowCircleLeft";
import UpdateGroupChatModal from "./UpdateGroupChatModal";
import "./ChattingStyling.css";
import { FormControl, Input } from "@mui/material";

const SingleChat = ({ fetchAgain, setFetchAgain }) => {
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [newMessage, setNewMessage] = useState();

  const { user, selectedChat, setSelectedChat } = ChatState();

  const sendMessage = () => {};
  const typingHandler = () => {};

  //console.log(selectedChat.chatName);
  return (
    <div className="SingleChatMain">
      {selectedChat ? (
        <>
          <text className="SingleChat">
            <ArrowCircleLeftIcon
              className="IconButton"
              onClick={() => setSelectedChat("")}
              sx={{
                display: { xs: "block", sm: "none" },
              }}
            />
            {!selectedChat.isGroupChat ? (
              <>
                <ArrowCircleLeftIcon
                  className="IconButton"
                  onClick={() => setSelectedChat("")}
                  sx={{
                    display: { xs: "none", sm: "block" },
                  }}
                />
                <img
                  src={selectedChat.users[1].pic}
                  alt={selectedChat.users[1].name}
                  style={{
                    width: "50px",
                    height: "50px",
                    borderRadius: "50%",
                    objectFit: "cover",
                    color: "black",
                    margin: "10px",
                  }}
                />
                {selectedChat.users[1].name.toUpperCase()}
              </>
            ) : (
              <>
                <ArrowCircleLeftIcon
                  className="IconButton"
                  onClick={() => setSelectedChat("")}
                  sx={{
                    display: { xs: "none", sm: "block" },
                  }}
                />
                {selectedChat.chatName.toUpperCase()}
                <UpdateGroupChatModal
                  fetchAgain={fetchAgain}
                  setFetchAgain={setFetchAgain}
                />
              </>
            )}
          </text>
          <Box
            display="flex"
            flexDirection="column"
            justifyContent="flex-end"
            padding="3px"
            width="80%"
            height="100%"
            borderRadius="large"
            overflow="hidden"
          >
            {loading ? (
              <Box
                display="flex"
                justifyContent="center"
                alignItems="center"
                height="100%"
              >
                <img
                  style={{
                    width: "100%",
                    height: "auto",
                    maxHeight: "80vh",
                    paddingLeft: "50px",
                  }}
                  src="./Images/loading.gif"
                  alt="loading"
                />
              </Box>
            ) : (
              <div>{/* Messages */}</div>
            )}
            <FormControl
              onKeyDown={sendMessage}
              isRequired
              style={{ marginBottom: "61px" }}
              width="100%"
              fullWidth="true"
            >
              <Input
                sx={{ flexGrow: 1 }}
                varient="filled"
                placeholder="Enter a message"
                bg="#E0E0E0"
                width="100px"
                onChange={typingHandler}
                value={newMessage}
              />
            </FormControl>
          </Box>
        </>
      ) : (
        <Box
          display="flex"
          alignItems="center"
          justifyContent="center"
          height="100%"
        >
          <text fontSize="28px">Click on a user to start chatting</text>
        </Box>
      )}
    </div>
  );
};

export default SingleChat;
