import { Tooltip, Button } from "@mui/material";
import Box from "@mui/material/Box";
import React, { useState } from "react";
import "./ChattingStyling.css";
import { ChatState } from "../Context/ChatProvider";
import { useNavigate } from "react-router-dom";
import Drawer from "@mui/material/Drawer";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import { ListItemIcon, ListItemText, TextField } from "@material-ui/core";

import SearchIcon from "@mui/icons-material/Search";

const SideDrawer = () => {
  const [search, setSearch] = useState("");
  const [searchResult, setSearchResult] = useState([]);
  const [loading, setLoading] = useState(false);
  const [loadingChat, setLoadingChat] = useState();
  const [open, setOpen] = useState(false);

  const toggleDrawer = (isOpen) => (event) => {
    if (
      event.type === "keydown" &&
      (event.key === "Tab" || event.key === "Shift")
    ) {
      return;
    }

    setOpen(isOpen);
  };

  const navigate = useNavigate();

  const user = ChatState();

  const logoutHandler = () => {
    localStorage.removeItem("userInfo");
    navigate("/");
  };

  return (
    <>
      <Box
        display="flex"
        justifyContent="space-between"
        alignItems="center"
        backgroundColor="white"
        width="98.5%"
        height="auto"
        padding="5px 10px 5px 10px"
      >
        <Tooltip label="Search Users to Chat" hasArrow placement="bottom-end">
          <Button varient="ghost" onClick={toggleDrawer(true)}>
            <img
              src="/Images/search.png"
              alt="search"
              style={{
                width: "44px",
                height: "auto",
              }}
            />
            <p style={{ padding: "2px" }}>Search Users</p>
          </Button>
        </Tooltip>
        <h2 style={{ color: "#da4ea2" }}>Chating Feature </h2>
        <div style={{ display: "flex" }}>
          <img src={user.pic} alt="Avatar" class="avatar" />
          <button className="logoutButton" onClick={logoutHandler}>
            Logout
          </button>
        </div>
      </Box>
      <div className="drawerMain">
        <Drawer anchor="left" open={open} onClose={toggleDrawer(false)}>
          <ListItem>
            <ListItemIcon>
              <SearchIcon />
            </ListItemIcon>
            <TextField
              placeholder="Search by name or email"
              className="drawer"
              value={search}
              onChange={(e) => setSearch(e.target.value)}
            />
            <Button>Go</Button>
          </ListItem>
        </Drawer>
      </div>
    </>
  );
};

export default SideDrawer;
