import React from 'react';
import styled from 'styled-components';



const Section = styled.div`
  
.card {
    width: 190px;
    height: 254px;
    background: #07182E;
    position: relative;
    display: inline-flex;
    place-content: center;
    place-items: center;
    overflow: hidden;
    border-radius: 20px;
    justify-content: center;
  }
  
  .card h2 {
    z-index: 1;
    color: white;
    font-size: 2em;
  }
  
  .card::before {
    content: '';
    position: absolute;
    width: 100px;
    background-image: linear-gradient(180deg, rgb(0, 183, 255), rgb(255, 48, 255));
    height: 130%;
    animation: rotBGimg 3s linear infinite;
    transition: all 0.2s linear;
  }
  
  @keyframes rotBGimg {
    from {
      transform: rotate(0deg);
    }
  
    to {
      transform: rotate(360deg);
    }
  }
  
  .card::after {
    content: '';
    position: absolute;
    background: #07182E;
    ;
    inset: 5px;
    border-radius: 15px;
  }  
  /* .card:hover:before {
    background-image: linear-gradient(180deg, rgb(81, 255, 0), purple);
    animation: rotBGimg 3.5s linear infinite;
  } */
  
  
`;

function Card(props) {
  return (
    <Section>
    <div className="card">
      <h3>{props.title}</h3>
      <p>{props.description}</p>
    </div>
    </Section>
  );
}

export default function Cards() {
  const languages = [
    { title: 'Python', description: 'Python is a high-level programming language.' },
    { title: 'C++', description: 'C++ is a powerful, high-performance language.' },
    { title: 'JavaScript', description: 'JavaScript is a programming language used to create interactive effects within web browsers.' },
  ];

  return (
    <div>
      {languages.map((language, index) => (
        <Card key={index} title={language.title} description={language.description} />
      ))}
    </div>
  );
}
