import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import ReactQuill from "react-quill";
import "react-quill/dist/quill.snow.css";
const QuestionDetails = () => {
  const [question, setQuestion] = useState(null);
  const [commentInput, setCommentInput] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const { postId } = useParams();
  const storedData = localStorage.getItem("userInfo");
  const parsedData = JSON.parse(storedData);
  const [newComment, setNewComment] = useState("");
  const id = parsedData._id;
  const [updatedQuestion, setUpdatedQuestion] = useState(null); // new state variable
  const [showEditModal, setShowEditModal] = useState(false);

  const handleSubmitComment = async () => {
    try {
      setIsLoading(true);
      const token = parsedData.token;
      const pic = parsedData.pic;
      const newComment = { content: commentInput, pic: pic };
      const updatedQuestion = { ...question };
      updatedQuestion.comments.push(newComment);
      setQuestion(updatedQuestion);
      console.log(newComment);
      setCommentInput("");
      localStorage.setItem(
        `question_${postId}_comments`,
        JSON.stringify(updatedQuestion.comments)
      );
      const response = await axios.post(
        `/api/community/${postId}/comments`,
        newComment,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        }
      );
      fetchQuestion();
      setIsLoading(false);
    } catch (error) {
      setIsLoading(false);
      console.error("Failed to submit comment:", error);
    }
  };
  const fetchQuestion = async () => {
    try {
      const response = await axios.get(`/api/community/${postId}`);
      setQuestion(response.data);
    } catch (error) {
      console.error("Failed to fetch question:", error);
    }
  };
  useEffect(() => {
    fetchQuestion();
  }, [postId]);
  if (!question) {
    return <div>Loading...</div>;
  }

  const handleCommentInputChange = (event) => {
    setCommentInput(event.target.value);
  };
  const handleEditQuestion = () => {
    setShowEditModal(true);
    setUpdatedQuestion({ ...question }); // create a copy of the original question to modify
  };
  const handleSubmitEditedQuestion = async () => {
    try {
      setIsLoading(true);
      const token = parsedData.token;
      const response = await axios.put(
        `/api/community/${postId}`,
        updatedQuestion,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        }
      );
      setQuestion(updatedQuestion); // update the question state with the updated question
      setShowEditModal(false); // hide the edit modal
      setIsLoading(false);
    } catch (error) {
      setIsLoading(false);
      console.error("Failed to submit edited question:", error);
    }
  };
  const handleDeleteQuestion = async () => {
    try {
      setIsLoading(true);
      const token = parsedData.token;
      await axios.delete(`/api/community/${postId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setIsLoading(false);
      // redirect the user to the community page or other appropriate page
    } catch (error) {
      setIsLoading(false);
      console.error("Failed to delete question:", error);
    }
  };
  const handlePostComment = async () => {
    try {
      setIsLoading(true);
      const token = parsedData.token;
      const response = await axios.post(
        `/api/community/${postId}/comments`,
        { content: newComment },
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        }
      );
      const newCommentData = response.data;
      setQuestion({
        ...question,
        comments: [...question.comments, newCommentData],
      });
      setNewComment("");
      setIsLoading(false);
    } catch (error) {
      setIsLoading(false);
      console.error("Failed to post comment:", error);
    }
  };

  return (
    <div
      style={{
        backgroundColor: "white",
        borderRadius: "15px",
        padding: "15px",
      }}
    >
      <h2 style={{ color: "#da4ea2", display: "flex", alignItems: "center" }}>
        <img
          src="/Images/arrow.gif"
          alt="arrow"
          style={{
            marginRight: "10px",
            width: "40px",
            height: "40px",
            borderRadius: "50%",
          }}
        />
        {question.title}
      </h2>

      <div id="button-container"></div>
      <div
        style={{
          border: "1px solid black",
          padding: "15px",
          borderRadius: "5px",
          marginBottom: "20px",
        }}
      >
        <ReactQuill
          value={question.content}
          readOnly={true}
          modules={{
            toolbar: false,
          }}
        />
        <span style={{ paddingBottom: "7px" }}>
          <b style={{ color: "#da4ea2" }}>Date:</b>{" "}
          {new Date(question.createdAt).toLocaleString()}
        </span>
      </div>
      <button onClick={handleEditQuestion}>Edit Question</button>
      <button onClick={handleDeleteQuestion}>Delete Question</button>
      {showEditModal && (
        <div>
          <input
            type="text"
            value={updatedQuestion.title}
            onChange={(event) =>
              setUpdatedQuestion({
                ...updatedQuestion,
                title: event.target.value,
              })
            }
          />
          <ReactQuill
            value={updatedQuestion.content}
            onChange={(value) =>
              setUpdatedQuestion({ ...updatedQuestion, content: value })
            }
          />
          <button onClick={handleSubmitEditedQuestion}>Save</button>
          <button onClick={() => setShowEditModal(false)}>Cancel</button>
        </div>
      )}
      <h3>{question.comments.length} Answers</h3>
      {question.comments.map((comment) => (
        <div
          key={comment._id} // Assign a unique key prop to each rendered comment
          style={{
            padding: "15px",
            borderStyle: "solid",
            borderColor: "black",
            margin: "5px",
            borderWidth: "1px",
            borderRadius: "15px",
          }}
        >
          <div
            style={{
              display: "flex",
              alignItems: "center",
              marginBottom: "10px",
            }}
          >
            <img
              src={comment.pic}
              alt="profile"
              style={{
                width: "5%",
                height: "auto",
                borderRadius: "50%",
                marginRight: "10px",
              }}
            />
            <div dangerouslySetInnerHTML={{ __html: comment.content }}></div>
          </div>
          <span style={{ paddingBottom: "7px" }}>
            <b style={{ color: "#da4ea2" }}>Date:</b>{" "}
            {new Date(comment.createdAt).toLocaleString()}
          </span>
        </div>
      ))}
      <div
        style={{
          marginTop: "20px",
          alignItems: "center",
        }}
      >
        <h2 style={{ color: "#da4ea2" }}>Add your Comment here</h2>
        <ReactQuill
          value={commentInput}
          onChange={setCommentInput}
          placeholder="Content"
          style={{
            backgroundColor: "white",
          }}
          modules={{
            toolbar: [
              [{ header: [1, 2, false] }],
              ["bold", "italic", "underline", "strike", "blockquote"],
              [
                { list: "ordered" },
                { list: "bullet" },
                { indent: "-1" },
                { indent: "+1" },
              ],
              ["link", "image"],
              ["clean"],
            ],
          }}
        />
        <button
          onClick={handleSubmitComment}
          disabled={!commentInput}
          style={{
            backgroundColor: "#da4ea2",
            color: "white",
            padding: "10px",
            borderRadius: "5px",
            border: "none",
            cursor: "pointer",
          }}
        >
          {isLoading ? "Submitting..." : "Submit"}
        </button>
      </div>
    </div>
  );
};
export default QuestionDetails;
