import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
const CourseDetails = () => {
  const [course, setCourse] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const { courseId } = useParams();

  const fetchQuestion = async () => {
    try {
      const response = await axios.get(`/api/courses/${courseId}`);
      setCourse(response.data);
    } catch (error) {
      console.error("Failed to fetch question:", error);
    }
  };
  useEffect(() => {
    fetchQuestion();
  }, [courseId]);
  if (!course) {
    return <div>Loading...</div>;
  }
  console.log(course);
  return (
    <div style={{ color: "white", display: "flex", flexDirection: "column" }}>
      {course.title}
      {course.description}
      {course.teacherInfo[0].name}
      {course.teacherInfo[0].pic}
      {course.teacherInfo[0].email}
      {course.thumbnailUrl}
      {course.comments.map((comment) => comment.content)}
      {course.comments.map((comment) => comment.user.name)}

      {course.enrollments[0].studentInfo.name}
    </div>
  );
};

export default CourseDetails;
