import React, { useState, useEffect } from "react";
import * as FaIcons from "react-icons/fa";
import * as AiIcons from "react-icons/ai";

import { Link } from "react-router-dom";
import { SidebarData } from "./SidebarData";
import "./Dashboard.css";
import { useParams } from "react-router-dom";
import { IconContext } from "react-icons";
import axios from "axios";

function Dashboard() {
  const [sidebar, setSidebar] = useState(true);
  const [userProfile, setUserProfile] = useState({});
  const { userId } = useParams(); // Retrieve user ID from URL parameter
  const [userData, setUserData] = useState(null);
  const [posts, setPosts] = useState(null);
  const showSidebar = () => setSidebar(!sidebar);
  const storedData = localStorage.getItem("userInfo");
  //console.log(storedData);
  const parsedData = JSON.parse(storedData);
  const token = parsedData.token;
  const Id = parsedData._id;
  console.log(Id);
  // console.log(token);
  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const config = {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        };

        const response = await axios.get(`/api/user/profile`, config); // Fetch user data using user ID
        setUserData(response.data);
        const questions = await axios.get("/api/community/allQuestions");
        setPosts(questions.data);
        //console.log(questions.data[0]._id);
        {
          questions
            .filter((post) => post.author._id === Id)
            .map(
              (post) => console.log(post)
              /*<div key={post._id}>
                  <h3>{post.title}</h3>
                  <p>{post.content}</p>
                </div>*/
            );
        }
      } catch (error) {
        console.error("Failed to fetch user data:", error);
      }
    };

    fetchUserData();
  }, [userId]);
  //console.log(userData);

  return (
    <div>
      {/* First Column */}

      <div>
        <IconContext.Provider value={{ color: "#da4ea2" }}>
          <div className="navbar">
            <Link to="#" className="menu-bars">
              <FaIcons.FaBars onClick={showSidebar} />
            </Link>
          </div>
          <nav className={sidebar ? "nav-menu active" : "nav-menu"}>
            <ul className="nav-menu-items" onClick={showSidebar}>
              <li className="navbar-toggle">
                <Link to="#" className="menu-bars">
                  <AiIcons.AiOutlineClose />
                </Link>
              </li>
              {SidebarData.map((item, index) => {
                return (
                  <li key={index} className={item.cName}>
                    <Link to={item.path}>
                      {item.icon}
                      <span>{item.title}</span>
                    </Link>
                  </li>
                );
              })}
            </ul>
          </nav>
        </IconContext.Provider>
      </div>

      {/* 2nd Column */}
      <div className="dashboard">
        <div>
          <div className="dashboardMainCard">
            <div className="dashboardMainCardleft">
              <h2>Welcome back to Code School</h2>
              <div>
                <p>
                  Learn something new today with the most attractive and new way
                </p>

                <p style={{ color: "#da4ea2" }}>New Day New Journey</p>
              </div>
            </div>
            <div className="dashboardMainCardRight">
              <img src="./Images/hand.png" alt="hand" />
            </div>
          </div>
          <div className="dashboardSecandoryCards">
            <h2>Questions asked</h2>
          </div>
        </div>

        {/* 3rd Column */}
        <div className="dashboardProfile">
          <div>
            {userData ? (
              <div>
                <img
                  src={userData.pic}
                  alt="userPic"
                  style={{
                    width: "40%",
                    borderRadius: "50%",
                    marginLeft: "30%",
                    marginBottom: "5%",
                  }}
                />
                <div style={{}}>
                  <p
                    style={{
                      paddingLeft: "20px",
                      borderBottom: "1px solid white",
                      cursor: "pointer",
                    }}
                  >
                    <b style={{ color: "#da4ea2", display: "inline-flex" }}>
                      Name:
                    </b>
                    <p style={{ marginLeft: "20px", display: "inline-flex" }}>
                      {userData.name}
                    </p>
                  </p>
                  <p
                    style={{
                      paddingLeft: "20px",
                      borderBottom: "1px solid white",
                      cursor: "pointer",
                    }}
                  >
                    <b style={{ color: "#da4ea2", display: "inline-flex" }}>
                      Email:
                    </b>
                    <p style={{ marginLeft: "20px", display: "inline-flex" }}>
                      {userData.email}
                    </p>
                  </p>
                  <p
                    style={{
                      paddingLeft: "20px",
                      borderBottom: "1px solid white",
                      cursor: "pointer",
                    }}
                  >
                    <b style={{ color: "#da4ea2", display: "inline-flex" }}>
                      Role:
                    </b>
                    <p style={{ marginLeft: "20px", display: "inline-flex" }}>
                      {userData.role}
                    </p>
                  </p>
                  <p style={{ paddingLeft: "20px" }}>
                    <b style={{ color: "#da4ea2", display: "inline-flex" }}>
                      Id:
                    </b>
                    <p style={{ marginLeft: "20px", display: "inline-flex" }}>
                      {userData._id}
                    </p>
                  </p>
                </div>
              </div>
            ) : (
              <p>Loading user data...</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
