import React, { useState } from "react";
import "./Courses.css";
const UploadCourses = () => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [pic, setPic] = useState("");
  const [video, setVideo] = useState("");
  const [videos, setVideos] = useState([]);
  const [loading, setLoading] = useState(false);

  const postDetails = async (pics, videos) => {
    setLoading(true);
    setPic(pics);
    setVideo(videos);

    if (!pics || !videos) {
      window.alert("Invalid file format");
      setLoading(false);
      return;
    }

    if (
      pics.type !== "image/jpeg" ||
      pics.type !== "image/png" ||
      pics.type !== "image/jpg"
    ) {
      window.alert("Invalid thumbnail format");
      setLoading(false);
      return;
    }

    if (videos.type !== "video/mp4") {
      window.alert("Invalid video format");
      setLoading(false);
      return;
    }

    const picData = new FormData();
    picData.append("file", pics);
    picData.append("upload_preset", "Code School");
    picData.append("cloud_name", "talha1244");

    const videoData = new FormData();
    videoData.append("file", videos);
    videoData.append("upload_preset", "Code School");
    videoData.append("cloud_name", "talha1244");

    try {
      // Upload the thumbnail
      const picResponse = await fetch(
        "https://api.cloudinary.com/v1_1/talha1244/image/upload",
        {
          method: "POST",
          body: picData,
        }
      );
      const picResult = await picResponse.json();
      console.log("Thumbnail uploaded successfully:", picResult);

      // Upload the video
      const videoResponse = await fetch(
        "https://api.cloudinary.com/v1_1/talha1244/video/upload",
        {
          method: "POST",
          body: videoData,
        }
      )
        .then((res) => res.json())
        .then((data) => {
          setVideos([...videos, data.url]);
          setLoading(false);
        });
      const videoResult = await videoResponse.json();
      console.log("Video uploaded successfully:", videoResult);

      setPic(picResult.secure_url);
      setVideo(videoResult.secure_url);
      setLoading(false);
    } catch (err) {
      console.log("Error uploading files:", err);
      setLoading(false);
    }
  };

  const submitHandler = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const config = {
        headers: {
          "Content-Type": "application/json",
        },
      };
      const data = {
        title,
        description,
        pic,
        videos,
      };

      setTimeout(() => {
        // perform your action here after waiting for 10 seconds
        console.log(data);
        window.alert("Posted Successful");
        setLoading(false);
        localStorage.setItem("userInfo", JSON.stringify(data));
      }, 10000);
    } catch (error) {
      console.log(error);
      window.alert("Invalid Registration", error);
      setLoading(false);
    }
  };

  return (
    <div className="MainUploadCourseBlock">
      <div className="ChildUploadCourseBlock">
        <h2 style={{ color: "#da4ea2", textAlign: "center" }}>
          Upload Your Courses Here!
        </h2>
        <form>
          <label htmlFor="title">Title:</label>
          <input
            name="title"
            type="text"
            id="title"
            autoComplete="off"
            value={title}
            placeholder="Enter your title here..."
            onChange={(e) => setTitle(e.target.value)}
            required
          />
          <br />
          <label htmlFor="description">Description:</label>
          <textarea
            name="description"
            id="description"
            autoComplete="off"
            value={description}
            placeholder="Enter your description here..."
            onChange={(e) => setDescription(e.target.value)}
            required
            style={{
              minWidth: "47%",
              minHeight: "50%",
              fontSize: "16px",
              display: "flex",
              borderRadius: "5px",
              marginLeft: "12px",
            }}
          />
          <br />
          <label htmlFor="Thumbnail">Thumbnail:</label>
          <input
            name="Thumbnail"
            type="file"
            id="file"
            defaultValue={pic}
            accept="image/*"
            onChange={(e) => postDetails(e.target.files[0])}
            required
          />
          <br />
          <label htmlFor="videos">videos:</label>
          <input
            name="video"
            type="file"
            id="video"
            accept="video/*"
            defaultValue={video}
            onChange={(e) => postDetails(e.target.files[0])}
            required
            multiple
          />
          <button
            type="submit"
            className="secandorybutton"
            isLoading={loading}
            onClick={submitHandler}
          >
            {loading ? "Loading..." : "Submit"}
          </button>
        </form>
      </div>
    </div>
  );
};

export default UploadCourses;
