import React, { useState } from "react";

import Button from "@material-ui/core/Button";
import axios from "axios";
import "./Courses.css";

const UploadCourses = () => {
  const [videos, setVideos] = useState([]); // State variable to hold uploaded videos
  const [title, setTitle] = useState("");
  const [thumbnailUrl, setThumbnailUrl] = useState("");
  const [description, setDescription] = useState("");
  const [loading, setLoading] = useState(false);
  const [videoTitle, setVideoTitle] = useState("");
  const [videoDescription, setVideoDescription] = useState("");
  // Function to handle image uploads
  const postImageDetails = (image) => {
    setLoading(true);
    setThumbnailUrl(image);
    if (image === undefined) {
      console.log("error");
      setLoading(false);
      return;
    }
    if (
      image.type === "image/jpeg" ||
      image.type === "image/png" ||
      image.type === "image/jpg"
    ) {
      const data = new FormData();
      data.append("file", image);
      data.append("upload_preset", "Code School");
      data.append("cloud_name", "talha1244");
      fetch("https://api.cloudinary.com/v1_1/talha1244/image/upload", {
        method: "POST",
        body: data,
      })
        .then((res) => res.json())
        .then((data) => {
          setThumbnailUrl(data.url);
          setLoading(false);
        })
        .catch((err) => {
          console.log(err);
          setLoading(false);
        });
    }
  };
  // Function to handle video uploads
  const postVideoDetails = (video) => {
    setLoading(true);
    setVideos([...videos, video]);
    if (video === undefined) {
      console.log("error");
      setLoading(false);
      return;
    }
    if (
      video.type === "video/mp4" ||
      video.type === "video/quicktime" ||
      video.type === "video/x-msvideo" ||
      video.type === "video/x-ms-wmv" ||
      video.type === "video/webm" ||
      video.type === "video/ogg"
    ) {
      const data = new FormData();
      data.append("file", video);
      data.append("upload_preset", "Code School");
      data.append("cloud_name", "talha1244");
      fetch("https://api.cloudinary.com/v1_1/talha1244/video/upload", {
        method: "POST",
        body: data,
      })
        .then((res) => res.json())
        .then((data) => {
          setVideos([...videos, data.url]);
          setLoading(false);
        })
        .catch((err) => {
          console.log(err);
          setLoading(false);
        });
    }
  };
  // Function to handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);
    const videoData = videos.map((video) => ({
      title: videoTitle,
      description: videoDescription,
      videosUrl: video,
    }));
    const data = {
      title,
      description,
      thumbnailUrl,
      videos: videoData,
    };
    console.log(data);

    // Get the authentication token from wherever it's stored in your app
    const storedData = localStorage.getItem("userInfo"); // assuming it's stored in localStorage

    // Parse the string back to an object
    const parsedData = JSON.parse(storedData);

    // Access the token and userId values
    const token = parsedData.token;
    //console.log(token);
    axios
      .post("/api/courses", data, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
      })
      .then((res) => {
        console.log(res);
        setLoading(false);
        // Do something after successful submission
      })
      .catch((err) => {
        console.log(err);
        setLoading(false);
      });
  };
  return (
    <div className="MainUploadCourseBlock">
      <div className="ChildUploadCourseBlock">
        <h2 style={{ color: "#da4ea2", textAlign: "center" }}>
          Upload Courses
        </h2>
        <form onSubmit={handleSubmit}>
          <label htmlFor="title">Title:</label>

          <input
            name="title"
            type="text"
            id="title"
            autoComplete="off"
            value={title}
            placeholder="Enter your title here..."
            onChange={(e) => setTitle(e.target.value)}
            required
          />
          <br />
          <label htmlFor="description">Description:</label>

          <textarea
            name="description"
            id="description"
            autoComplete="off"
            value={description}
            placeholder="Enter your description here..."
            onChange={(e) => setDescription(e.target.value)}
            required
            style={{
              minWidth: "47%",
              minHeight: "50%",
              fontSize: "16px",
              display: "flex",
              borderRadius: "5px",
              marginLeft: "12px",
            }}
          />
          <br />

          <div>
            <label htmlFor="Thumbnail">Thumbnail:</label>
            <input
              type="file"
              accept="image/*"
              onChange={(e) => postImageDetails(e.target.files[0])}
            />
          </div>
          <div style={{ border: "1px solid black" }}>
            <label htmlFor="title"> Video Title:</label>
            <input
              name="title"
              type="text"
              id="title"
              autoComplete="off"
              value={videoTitle}
              placeholder="Enter your title here..."
              onChange={(e) => setVideoTitle(e.target.value)}
              required
            />
            <br />
            <label htmlFor="description"> Video Description:</label>
            <textarea
              name="description"
              id="description"
              autoComplete="off"
              value={videoDescription}
              placeholder="Enter your description here..."
              onChange={(e) => setVideoDescription(e.target.value)}
              required
              style={{
                minWidth: "47%",
                minHeight: "50%",
                fontSize: "16px",
                display: "flex",
                borderRadius: "5px",
                marginLeft: "12px",
              }}
            />
            <br />
            <div>
              <label htmlFor="videos">videos:</label>
              <input
                type="file"
                accept="video/*"
                onChange={(e) => postVideoDetails(e.target.files[0])}
                multiple
              />
            </div>
          </div>
          <Button
            variant="contained"
            type="submit"
            disabled={loading}
            className="secandorybutton"
          >
            {loading ? "Uploading..." : "Upload"}
          </Button>
        </form>
      </div>
    </div>
  );
};
export default UploadCourses;
