import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const QuestionDetails = () => {
  const [question, setQuestion] = useState(null);
  const [commentInput, setCommentInput] = useState("");
  const { postId } = useParams();

  useEffect(() => {
    const fetchQuestion = async () => {
      try {
        const response = await axios.get(`/api/community/${postId}`);
        setQuestion(response.data);
      } catch (error) {
        console.error("Failed to fetch question:", error);
      }
    };

    fetchQuestion();
  }, [postId]);

  const handleCommentInputChange = (event) => {
    setCommentInput(event.target.value);
  };

  const handleSubmitComment = async () => {
    try {
      const storedData = localStorage.getItem("userInfo"); // assuming it's stored in localStorage

      // Parse the string back to an object
      const parsedData = JSON.parse(storedData);

      // Access the token value
      const token = parsedData.token;
      const pic = parsedData.pic;
      console.log(token);
      console.log(pic);
      // Create a new comment object with the updated content
      const newComment = { content: commentInput };

      // Send the request with the new comment object
      const response = await axios.post(
        `/api/community/${postId}/comments`,
        newComment,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json", // Set content type to application/json
          },
        }
      );

      setQuestion((prevQuestion) => ({
        ...prevQuestion,
        comments: [...prevQuestion.comments, response.data],
      }));

      setCommentInput("");
    } catch (error) {
      console.error("Failed to submit comment:", error);
    }
  };

  if (!question) {
    return <div>Loading...</div>;
  }

  console.log(question);

  return (
    <div
      style={{
        backgroundColor: "white",
        borderRadius: "15px",
        padding: "15px",
      }}
    >
      <h2 style={{ color: "black", display: "flex", alignItems: "center" }}>
        <img
          src="/Images/arrow.gif"
          alt="arrow"
          style={{
            marginRight: "10px",
            width: "40px",
            height: "40px",
            borderRadius: "50%",
          }}
        />
        {question.title}
      </h2>
      <div>
        <b>Description:</b>
        <div dangerouslySetInnerHTML={{ __html: question.content }}></div>
      </div>
      <p>
        <b>Answers:</b>
      </p>
      {question.comments.map((comment) => (
        <div
          key={comment._id}
          dangerouslySetInnerHTML={{ __html: comment.content }}
        ></div>
      ))}
      <div>
        <textarea
          value={commentInput}
          onChange={handleCommentInputChange}
          placeholder="Enter your comment..."
        ></textarea>
        <button onClick={handleSubmitComment}>Submit</button>
      </div>
    </div>
  );
};

export default QuestionDetails;
