import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    height: "60vh",
    backgroundColor: "#f5f5f5",
  },
  paper: {
    padding: theme.spacing(2),
    maxWidth: "500px",
    width: "80%",
  },
  textField: {
    marginBottom: theme.spacing(2),
  },
  button: {
    backgroundColor: "#da4ea2",
    color: "white",
    "&:hover": {
      backgroundColor: "#c6387e",
    },
  },
}));

const UploadCourses = () => {
  const classes = useStyles();
  const [videos, setVideos] = useState([]); // State variable to hold uploaded videos

  // Function to handle file input change
  const handleFileInputChange = (event) => {
    const files = Array.from(event.target.files); // Convert FileList object to array
    setVideos([...videos, ...files]); // Add uploaded files to the state array
  };

  // Function to render the list of uploaded videos
  const renderVideos = () => {
    return (
      <ul>
        {videos.map((video, index) => (
          <li key={index}>{video.name}</li>
        ))}
      </ul>
    );
  };

  return (
    <div
      style={{
        backgroundColor: "#f5f5f5",
        textAlign: "center",
        paddingTop: "40px",
      }}
    >
      <h2 style={{ color: "#da4ea2" }}>Got Some New Skills?</h2>
      <h3 style={{ color: "#da4ea2" }}>
        Help others too and share your knowledge
      </h3>
      <div className={classes.root}>
        <Paper className={classes.paper}>
          <form>
            <TextField
              fullWidth
              variant="outlined"
              label="Course Title"
              className={classes.textField}
            />
            <TextField
              fullWidth
              variant="outlined"
              label="Course Description"
              className={classes.textField}
              multiline
              rows={4}
            />
            <TextField
              fullWidth
              variant="outlined"
              label="Course Image URL"
              className={classes.textField}
            />
            <input
              type="file"
              accept="video/*"
              multiple
              onChange={handleFileInputChange}
            />
            {renderVideos()}
            <Button variant="contained" className={classes.button}>
              Upload
            </Button>
          </form>
        </Paper>
      </div>
    </div>
  );
};

export default UploadCourses;
