import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import axios from "axios";
import "./Courses.css";
const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    height: "60vh",
    backgroundColor: "#f5f5f5",
  },
  paper: {
    padding: theme.spacing(2),
    maxWidth: "500px",
    width: "80%",
  },
  textField: {
    marginBottom: theme.spacing(2),
  },
  button: {
    backgroundColor: "#da4ea2",
    color: "white",
    "&:hover": {
      backgroundColor: "#c6387e",
    },
  },
}));
const UploadCourses = () => {
  const classes = useStyles();
  const [videos, setVideos] = useState([]); // State variable to hold uploaded videos
  const [title, setTitle] = useState("");
  const [pic, setPic] = useState("");
  const [description, setDescription] = useState("");
  const [loading, setLoading] = useState(false);
  // Function to handle image uploads
  const postImageDetails = (image) => {
    setLoading(true);
    setPic(image);
    if (image === undefined) {
      console.log("error");
      setLoading(false);
      return;
    }
    if (image.type === "image/jpeg" || image.type === "image/png") {
      const data = new FormData();
      data.append("file", image);
      data.append("upload_preset", "Code School");
      data.append("cloud_name", "talha1244");
      fetch("https://api.cloudinary.com/v1_1/talha1244/image/upload", {
        method: "POST",
        body: data,
      })
        .then((res) => res.json())
        .then((data) => {
          setPic(data.url);
          setLoading(false);
        })
        .catch((err) => {
          console.log(err);
          setLoading(false);
        });
    }
  };
  // Function to handle video uploads
  const postVideoDetails = (video) => {
    setLoading(true);
    setVideos([...videos, video]);
    if (video === undefined) {
      console.log("error");
      setLoading(false);
      return;
    }
    if (
      video.type === "video/mp4" ||
      video.type === "video/quicktime" ||
      video.type === "video/x-msvideo" ||
      video.type === "video/x-ms-wmv" ||
      video.type === "video/webm" ||
      video.type === "video/ogg"
    ) {
      const data = new FormData();
      data.append("file", video);
      data.append("upload_preset", "Code School");
      data.append("cloud_name", "talha1244");
      fetch("https://api.cloudinary.com/v1_1/talha1244/video/upload", {
        method: "POST",
        body: data,
      })
        .then((res) => res.json())
        .then((data) => {
          setVideos([...videos, data.url]);
          setLoading(false);
        })
        .catch((err) => {
          console.log(err);
          setLoading(false);
        });
    }
  };
  // Function to handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);
    const data = {
      title,
      pic,
      description,
      videos,
    };
    console.log(data);
    /*
    axios
      .post("/api/upload", data)
      .then((res) => {
        console.log(res);
        setLoading(false);
        // Do something after successful submission
      })
      .catch((err) => {
        console.log(err);
        setLoading(false);
      });
      */
  };
  return (
    <div className="MainUploadCourseBlock">
      <div className="ChildUploadCourseBlock">
        <h2 style={{ color: "#da4ea2", textAlign: "center" }}>
          Upload Courses
        </h2>
        <form onSubmit={handleSubmit}>
          <TextField
            label="Course Title"
            variant="outlined"
            fullWidth
            required
            className={classes.textField}
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
          <TextField
            label="Course Description"
            variant="outlined"
            fullWidth
            required
            className={classes.textField}
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
          <div>
            <input
              type="file"
              onChange={(e) => postImageDetails(e.target.files[0])}
            />
          </div>
          <div>
            <input
              type="file"
              onChange={(e) => postVideoDetails(e.target.files[0])}
              multiple
            />
          </div>
          <Button
            variant="contained"
            type="submit"
            className={classes.button}
            disabled={loading}
          >
            {loading ? "Uploading..." : "Upload"}
          </Button>
        </form>
      </div>
    </div>
  );
};
export default UploadCourses;
