  const { user } = ChatState();

  return (
    <div
      style={{
        width: "100%",
        backgroundImage: "url('../public/Images/bg.jpeg')",
        backgroundRepeat: "no-repeat",
        backgroundSize: "cover",
        backgroundPosition: "center",
      }}
    >
      {/*user && <SideDrawer/>*/}
      <Box bg="blue.500">
        {/*user && <MyChats/>*/}
        {/*user && <ChatBox/>*/}
      </Box>
    </div>
  );
};

export default ChatHome;
