import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import { Prism as SyntaxHighlighter } from "react-syntax-highlighter";
import { solarizedlight } from "react-syntax-highlighter/dist/esm/styles/prism"; // Import the desired syntax highlighting style

const QuestionDetails = () => {
  const [question, setQuestion] = useState(null);
  const { postId } = useParams();

  useEffect(() => {
    const fetchQuestion = async () => {
      try {
        const response = await axios.get(`/api/community/${postId}`);
        console.log(response.data);
        setQuestion(response.data);
      } catch (error) {
        console.error("Failed to fetch question:", error);
      }
    };

    fetchQuestion();
  }, [postId]);

  if (!question) {
    return <div>Loading...</div>;
  }

  if (typeof question.content !== "string") {
    return <div>Invalid question content</div>;
  }

  return (
    <div>
      <h2>{question.title}</h2>
      <p>
        <b>Description:</b>
      </p>
      <SyntaxHighlighter
        language="python" // Set the language for syntax highlighting
        style={solarizedlight} // Set the desired syntax highlighting style
      >
        {question.content}
      </SyntaxHighlighter>
      <p>
        <b>Answers:</b>
      </p>
      {question.comments.map((comment) => (
        <div key={comment._id}>
          <p>{comment.content}</p>
        </div>
      ))}
    </div>
  );
};

export default QuestionDetails;
