import { Tooltip, Button, Avatar } from "@mui/material";
import Box from "@mui/material/Box";
import React, { useState } from "react";
import "./ChattingStyling.css";
import { ChatState } from "../Context/ChatProvider";
import { useNavigate } from "react-router-dom";
import Drawer from "@mui/material/Drawer";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import axios from "axios";
import UserListItem from "../UserAvatar/UserListItem";
import { ListItemText, TextField } from "@material-ui/core";

import SearchIcon from "@mui/icons-material/Search";

const SideDrawer = () => {
  const [search, setSearch] = useState("");
  const [searchError, setSearchError] = useState("");
  const [searchResult, setSearchResult] = useState([]);
  const [loading, setLoading] = useState(false);
  const [loadingChat, setLoadingChat] = useState();
  const [open, setOpen] = useState(false);

  const toggleDrawer = (isOpen) => (event) => {
    if (
      event.type === "keydown" &&
      (event.key === "Tab" || event.key === "Shift")
    ) {
      return;
    }

    setOpen(isOpen);
  };

  const navigate = useNavigate();

  const { user, setSelectedChat, chats, setChats } = ChatState();

  const logoutHandler = () => {
    localStorage.removeItem("userInfo");
    navigate("/");
  };

  const handleSearch = async () => {
    if (!search.trim()) {
      setSearchError("Please enter something to search");
    } else {
      setSearchError("");
      setSearchResult([]);
      setLoading(true);

      try {
        const config = {
          headers: {
            authorization: `Bearer ${user.user.token}`,
          },
        };
        const { data } = await axios.get(
          `/api/user/auth?search=${search}`,
          config
        );

        setLoading(false);
        setSearchResult(data);
        console.log(data);
        console.log(data[4].pic);
        data.map((user) => console.log(user._id));
      } catch (error) {
        setLoading(false);
        setSearchError("Failed to load Search Results");
        console.log(error);
      }
    }
  };

  const accessChat = async (userId) => {
    console.log(userId);

    try {
      setLoadingChat(true);
      const config = {
        headers: {
          "Content-type": "application/json",
          authorization: `Bearer ${user.token}`,
        },
      };
      const { data } = await axios.post(`/api/chat`, { userId }, config);

      if (!chats.find((c) => c._id === data._id)) setChats([data, ...chats]);
      setSelectedChat(data);
      setLoadingChat(false);
      onClose();
    } catch (error) {
      toast({
        title: "Error fetching the chat",
        description: error.message,
        status: "error",
        duration: 5000,
        isClosable: true,
        position: "bottom-left",
      });
    }
  };

  return (
    <>
      <Box
        display="flex"
        justifyContent="space-between"
        alignItems="center"
        backgroundColor="white"
        width="98.5%"
        height="auto"
        padding="5px 10px 5px 10px"
      >
        <Tooltip label="Search Users to Chat" hasArrow placement="bottom-end">
          <Button variant="ghost" onClick={toggleDrawer(true)}>
            <img
              src="/Images/search.png"
              alt="search"
              style={{
                width: "44px",
                height: "auto",
              }}
            />
            <p style={{ padding: "2px" }}>Search Users</p>
          </Button>
        </Tooltip>
        <h2 style={{ color: "#da4ea2" }}>Chatting Feature</h2>
        <div style={{ display: "flex" }}>
          <Avatar src={user.user.pic} alt="Avatar" className="avatar" />

          <button className="logoutButton" onClick={logoutHandler}>
            Logout
          </button>
        </div>
      </Box>
      <div className="drawerMain">
        <Drawer anchor="left" open={open} onClose={toggleDrawer(false)}>
          <List>
            <ListItem>
              <TextField
                placeholder="Search by name or email"
                className="drawer"
                value={search}
                onChange={(e) => setSearch(e.target.value)}
              />
              <button className="searchButton" onClick={handleSearch}>
                <SearchIcon />
              </button>
            </ListItem>
            {searchResult.length > 0 && (
              <ListItem>
                <ListItemText
                  primary={searchResult.map((user) => user.username)}
                />
              </ListItem>
            )}
          </List>

          {loading ? (
            <p>Loading....</p>
          ) : (
            searchResult?.map((user) => (
              <UserListItem
                key={user._id}
                user={user}
                handleFunction={() => accessChat(user._id)}
              />
            ))
          )}
        </Drawer>
      </div>
    </>
  );
};

export default SideDrawer;
