import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const QuestionDetails = () => {
  const [question, setQuestion] = useState(null);
  const [commentInput, setCommentInput] = useState("");
  const [isLoading, setIsLoading] = useState(false); // Add a loading state
  const { postId } = useParams();

  const handleCommentInputChange = (event) => {
    setCommentInput(event.target.value);
  };

  const handleSubmitComment = async () => {
    try {
      setIsLoading(true); // Set loading state to true before making the request

      const storedData = localStorage.getItem("userInfo"); // assuming it's stored in localStorage

      // Parse the string back to an object
      const parsedData = JSON.parse(storedData);

      // Access the token value
      const token = parsedData.token;

      // Create a new comment object with the updated content
      const newComment = { content: commentInput };

      // Send the request with the new comment object
      const response = await axios.post(
        `/api/community/${postId}/comments`,
        newComment,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json", // Set content type to application/json
          },
        }
      );

      // Append the new comment to the comments array of the question object
      const updatedQuestion = { ...question };
      updatedQuestion.comments.push(response.data);

      // Update the question state with the updated question object
      setQuestion(updatedQuestion);

      setCommentInput("");

      // Update localStorage with the latest comments data
      localStorage.setItem(
        `question_${postId}_comments`,
        JSON.stringify(updatedQuestion.comments)
      );

      // Fetch question data again to refresh the comment list
      fetchQuestion();
      setIsLoading(false); // Set loading state to false after request is completed
    } catch (error) {
      console.error("Failed to submit comment:", error);
      setIsLoading(false); // Set loading state to false in case of error
    }
  };

  const fetchQuestion = async () => {
    try {
      const response = await axios.get(`/api/community/${postId}`);
      setQuestion(response.data);
    } catch (error) {
      console.error("Failed to fetch question:", error);
    }
  };

  useEffect(() => {
    fetchQuestion();
  }, [postId]);

  if (!question) {
    return <div>Loading...</div>;
  }

  return (
    <div
      style={{
        backgroundColor: "white",
        borderRadius: "15px",
        padding: "15px",
      }}
    >
      <h2 style={{ color: "black", display: "flex", alignItems: "center" }}>
        <img
          src="/Images/arrow.gif"
          alt="arrow"
          style={{
            marginRight: "10px",
            width: "40px",
            height: "40px",
            borderRadius: "50%",
          }}
        />
        {question.title}
      </h2>
      <div>
        <b>Description:</b>
        <div dangerouslySetInnerHTML={{ __html: question.content }}></div>
      </div>
      <p>
        <b>Answers:</b>
      </p>
      {question.comments.map((comment) => (
        <div
          key={comment._id} // assign a unique key prop
          dangerouslySetInnerHTML={{ __html: comment.content }}
        ></div>
      ))}

      <div>
        <textarea
          value={commentInput}
          onChange={handleCommentInputChange}
          placeholder="Add a comment..."
          rows="4"
          cols="50"
          style={{ marginTop: "10px" }}
        ></textarea>
        <button
          onClick={handleSubmitComment}
          style={{ marginTop: "10px", marginBottom: "20px" }}
        >
          {isLoading ? "Submitting..." : "Submit Comment"}
        </button>
      </div>
    </div>
  );
};

export default QuestionDetails;
